css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("community.blendernation.com") {

	
	mark{
	background:#ff0;
	color:#000
			}

  html{
	color:#222;
	font-family:Helvetica,Arial,sans-serif;
	font-size:14px;
	line-height:19px;
	background-color:#111;
	overflow-y:scroll
	    }
	.d-header {
    width: 100%;
    position: absolute;
    top: 0px;
    z-index: 1001;
    background-color: #292929;
    box-shadow: 0px 2px 4px -1px rgba(0, 0, 0, 0.25);
						}
	.select2-container .select2-choice{
	display:block;
	height:26px;
	padding:0 0 0 8px;
	overflow:hidden;
	position:relative;
	border:1px solid #aaa;
	white-space:nowrap;
	line-height:26px;
	color:#444;
	text-decoration:none;
	border-radius:4px;
	background-clip:padding-box;
	
	background-color:#111
						}
	.select2-container .select2-choice abbr{
	display:none;
	width:12px;
	height:12px;
	position:absolute;
	right:24px;
	top:8px;
	font-size:1px;
	text-decoration:none;
	border:0;
	background:url(http://techtikus.com/wp-content/uploads/2012/02/Cloud.jpg) right top no-repeat;
	cursor:pointer;
	outline:0
						}
	.select2-drop-mask{
	border:0;
	margin:0;
	padding:0;
	position:fixed;
	left:0;
	top:0;
	min-height:100%;
	min-width:100%;
	height:auto;
	width:auto;
	opacity:0;
	z-index:9998;
	background-color:#fff;
						}

.select2-drop{
	width:100%;
	margin-top:-1px;
	position:absolute;
	z-index:9999;
	top:100%;
	background:#111;
	color:#000;
	border:1px solid #aaa;
	border-top:0;
	border-radius:0 0 4px 4px;
	-webkit-box-shadow:0 4px 5px rgba(0,0,0,0.15);
	box-shadow:0 4px 5px rgba(0,0,0,0.15)
							}
	.select2-container .select2-choice .select2-arrow b{
	display:block;
	width:100%;
	height:100%;
	background:url(http://techtikus.com/wp-content/uploads/2012/02/Cloud.jpg) no-repeat 0 1px
}
	.select2-search input{
	width:100%;
	height:auto !important;
	min-height:26px;
	padding:4px 20px 4px 5px;
	margin:0;
	outline:0;
	font-family:sans-serif;
	font-size:1em;
	border:1px solid #aaa;
	border-radius:0;
	-webkit-box-shadow:none;
	box-shadow:none;
	background:#fff 
	}
	
.select2-search input.select2-active{
	background:#111 }


.select2-container-multi .select2-choices .select2-search-field input.select2-active{
	background:#111 !important
}

.select2-container-multi .select2-choices .select2-search-choice{
	padding:3px 5px 3px 18px;
	margin:3px 0 3px 5px;
	position:relative;
	line-height:13px;
	color:#333;
	cursor:default;
	border:1px solid #aaaaaa;
	border-radius:3px;
	-webkit-box-shadow:0 0 2px #fff inset,0 1px 0 rgba(0,0,0,0.05);
	box-shadow:0 0 2px #fff inset,0 1px 0 rgba(0,0,0,0.05);
	background-clip:padding-box;

	background-color:#444
}

.topic-list>tbody>tr:nth-child(odd){
	background-color:#444
	
}
.topic-list{
	width:100%;
	border-collapse:separate;
	border-spacing:0;
	border-top:1px solid #363535;
	background:rgba(255,255,255,0.8)
}
.topic-list .topic-excerpt{
	font-size:0.929em;
	margin-top:8px;
	color:#E4E4E4;
	word-wrap:break-word;
	line-height:1.4;
	padding-right:20px
}
.topic-list td{
	color:#31C877;
	font-size:1em
}
body .heatmap-low{
	color:#33C36A !important
}
.topic-list>tbody>tr:nth-child(even){
	background-color:#393434
}
.moderator .topic-body{
	background-color:#111
}
.topic-body{
	width:690px;
	float:left;
	position:relative;
	z-index:2;
	border-top:1px solid #E6E6E6;
	padding:12px 11px 15px 11px
}
#topic-title h1 a{
	color:#C9C9C9
}
.topic-list a.title{
	color:#CECCCC
}
.topic-list th{
	color:#181818;
	font-weight:normal;
	font-size:1em
}
.nav-pills>li>a{
	border:none;
	padding:5px 12px;
	color:#C2B3B3;
	font-size:1.143em;
	line-height:20px;
	transition:background .15s
}
  html{
	color:#e4e4e4; /* Text inside topic*/
	font-family:Helvetica,Arial,sans-serif;
	font-size:14px;
	line-height:19px;
	background-color:#111;
	overflow-y:scroll
}
#suggested-topics .topic-list>tbody>tr:nth-child(odd){
	background-color:#393939
}
#suggested-topics .topic-list>tbody>tr:nth-child(even){
	background-color:#514E4E
}
.d-dropdown {
    display: none;
    position: absolute;
    background: none repeat scroll 0% 0% #13265C;
    max-height: 417px;
    top: 100%;
    right: 0px;
    z-index: 1100;
    overflow: auto;
    border: 1px solid #0B67A2;
    padding: 5px;
    box-shadow: 0px 2px 26px rgba(248, 121, 53, 0.4);
}
.topic-map {
    margin: 20px 0px 0px;
    background: none repeat scroll 0% 0% #272727;
}
.topic-map .map .number, .topic-map .map i {
    color: #8C8585;
}
.topic-map .avatars, .topic-map .links, .topic-map .information {
    color: #AEADAD;
}
.extra-info-wrapper .topic-link {
    color: #FF9E4B;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
.embedded-posts .quote .title, .embedded-posts blockquote, .embedded-posts .onebox, .embedded-posts .onebox-result {
    background: none repeat scroll 0% 0% #6B6969;
    border-left: 5px solid #CACACA;
}
aside.quote .title { 
    color: #FF9E45;
    padding: 12px 12px 1px;
    -moz-user-select: none;
}
.embedded-posts {
    background: none repeat scroll 0% 0% #423939 !important;
}
.embedded-posts .topic-body {
    background: none repeat scroll 0% 0% rgb(54, 54, 54) !important;
    box-sizing: border-box;
    width: calc(100% - 60px);
    overflow: hidden;
    padding: 10px 15px 15px;
}
aside.quote .title {
    border-left: 5px solid #CFCFCF;
    background-color: #6B6969;
    color: rgb(255, 158, 69);
    padding: 12px 12px 1px;
    -moz-user-select: none;
}
blockquote {
    background-color: #272727;
    border-left: 5px solid #CFCFCF;
    overflow: hidden;
}
#reply-control .wmd-controls #wmd-input, #reply-control .wmd-controls #wmd-preview-scroller, #reply-control .wmd-controls #wmd-preview {
		background-color: #393939;
}
#reply-control .contents #wmd-input, #reply-control .contents #wmd-preview {
    color: rgb(255, 255, 255);
}
#reply-control #wmd-button-bar {
    border-bottom: 1px solid #E9E9E9;
    background-color: #352C2C;
}
#reply-control {
   background-color: #242424;
}
body .grippie {
    background: #353535;
}
#reply-control .toggler {
	color: #08C;
}
a {
    color: #27D5FB;
}
#notifications-dropdown.d-dropdown .read {
    background-color: #302C2C;
}

#notifications-dropdown.d-dropdown li span {
    color: #CBC2C2;
}
.badge-posts[href] {
    color: #39BC6A;
}
.heatmap-med a {
    color: #31C370 !important;
}
body .coldmap-low {
    color: #39BC66 !important;
}
.btn {
background: none repeat scroll 0% 0% #FF953B;
}
#topic-progress-expanded {
    border: 1px solid #E9E9E9;
    background: none repeat scroll 0% 0% rgba(255, 255, 255, 0.14);
    box-shadow: 5px 12px 25px rgba(61, 186, 255, 0.66);
}
nav.post-controls .show-replies {
    background: none repeat scroll 0% 0% #1A1A1A;
}
nav.post-controls button.create {
    color: #08C;
    background: none repeat scroll 0% 0% #414141;
}
#topic-progress {
    background-color: #303030;
   	color: rgb(144, 109, 82);
}
#topic-progress .bg {
		background-color: #FFA735;
}

.modal-inner-container {
    max-width: 710px;
    margin: 0px auto;
    background-color: rgba(255, 255, 255, 0.53);
    border: 1px solid #E9E9E9;
    box-shadow: 7px 2px 25px rgba(64, 59, 255, 0.8);
    background-clip: padding-box;
}

.user-main .user-content {
    padding: 10px 8px;
    background-color: #363535;
    border: 1px solid #322020;
}

body #pagedown-editor {
    background-color: #2C2020;
    border: 1px solid #1D1515;
}

body select, body textarea {
    font-size: 1em;
    color: #E4D4D4;
}

.user-preferences .static {
    color: #34C7E0;
}

.nav-stacked > li > a {
    color: #B6A1A1;
}

.nav-stacked {
    background: none repeat scroll 0% 0% rgb(42, 31, 31);
}

.nav-stacked > li {
    border-bottom: 1px solid rgb(24, 17, 17);
}

.user-main .user-stream .item {
    background-color: #1D1A1A;
    border-bottom: 1px solid #050202;
}	
	
.user-main .user-stream .excerpt {
    font-size: 1.1em;
    color: #D2C7C7;
}	
	
.user-main .user-stream .notification p span {
    color: #49E4A8;
}	
	
.paginated-topics-list .topic-list > tbody > tr:nth-child(2n+1) {
    background-color: rgb(38, 33, 33);
}	
	
	
	



}
