css:
@-moz-document url-prefix("http://www.chicagomanualofstyle.org") {

body {
  color:#000000; 
  background-color: #F1E9D2;
  font-family: TisaWebPro, Times Roman, Times, Georgia, serif;
  margin: 0;
  padding: 0;
}

#footer {
  font-family: Verdana;
  background-color: #F1E9D2;
  border-top: solid 3px #84b8c6;
  border-bottom: solid 0px #FF6A00;
  clear: both;
  width: 100%;
}

#main_content div.example {
        color: #626f74;          
        font-size: 14px;      
        background-color: #F1E9D2;
        font-family: TisaWebPro, Times Roman, Times, Georgia, serif;
        font-weight: normal;  /* added */
        font-style: normal; /* added */
        width:100%;
        line-height: 1.35em;    
}

body.ch12 #main_content div.example{  /*"Mathematics in Type"*/ 
        font-family: TisaWebPro, Times Roman, Times, Georgia, serif;
        font-size: 15px;
        line-height: 1.35em;
        color:#000000; 
        background-color: #F1E9D2;
        margin-left: 1em;
}

#main_content dl.hangtab {
        background-color: #F1E9D2;
        color: #626f74;
        font-family: TisaWebPro, Times Roman, Times, Georgia, serif;
        font-weight: normal;
        font-style: normal;
        line-height: 1.35em;
        font-size: 14px;
}

#main_content dl.nohangtab {
        background-color: #F1E9D2;
        color: #626f74;
        font-family: TisaWebPro, Times Roman, Times, Georgia, serif;
        font-weight: normal;
        font-style: normal;
        line-height: 1.35em;
        font-size: 14px;
}

#main_content dl.indenttab {
        background-color: #F1E9D2;
        color: #626f74;
        font-family: TisaWebPro, Times Roman, Times, Georgia, serif;
        font-weight: normal;
        font-style: normal;
        line-height: 1.35em;
        font-size: 14px;
}

#main_content p#trail span.carrot {
        font-family: Verdana, Arial, Helvetica, sans-serif;
        background-color: #F1E9D2;
        color: #626f74;
        font-family: TisaWebPro, Times Roman, Times, Georgia, serif;
        font-weight: normal;  /* added */
        font-style: normal; /* added */
        font-size: 70%;
        margin: 0 0.6em 0 1.0em;

}

html>body span.inline_or, span.inline_but, span.inline_other, span.inline_not, span.inline_other {
        background-color: #F1E9D2;
        font-weight: normal;
        color:#000000;
        font-style: italic;
}

html>body span.or, span.but, span.other, span.not, span.other {
        background-color: #F1E9D2;
        font-weight: normal;
        margin-left: 1em;
        color: #000000;
        line-height: 300%;
        font-size: 90%;
        font-style: italic;
}

