}

#main_content div.poem {
        background-color: #F1E9D2;
        color: #626f74;
        font-size: 14px;
        font-family: TisaWebPro-Bold, Times Roman, Times, Georgia, serif;
        font-weight: bold;
        line-height: 1.35em;
        width: 100%;
