css:
@-moz-document domain("mightytext.net") {
body {
margin: 0;
font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
font-size: 14px;
line-height: 20px;
color: #F20000;
background: #222;
}

#sideNavigation {
background: #222;
}

::-webkit-scrollbar-thumb {
border-radius: 2px;
background-color: #671c1c;
-webkit-box-shadow: 0 0 1px #222;
}

.contentPanelHeaderText, .contentPanelHeaderNum {
font-weight: 600;
color: red;
}

.contentPanelHeaderText {
background: #222;
}

.contentPanelHeader {
background-color: #222;
}

.threadConversationHolder {
width: 100%;
height: 220px;
overflow: hidden;
overflow-y: auto;
background: #222;
}

.textResponse {
font-size: 13px;
border-radius: 0;
background-color: #222;
border: red;
-webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
-moz-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
-webkit-transition: border linear 0.2s, box-shadow linear 0.2s;
-moz-transition: border linear 0.2s, box-shadow linear 0.2s;
-o-transition: border linear 0.2s, box-shadow linear 0.2s;
transition: border linear 0.2s, box-shadow linear 0.2s;
}

.responseArea {
padding: 6px;
position: relative;
background: #671C1C;
}

#powerViewThreadsHolder {
position: relative;
z-index: 5;
background: rgb(29, 2, 2);
}

.new-emoji-selector {
background-color: #222;
border: 0px;
border-radius: 4px;
}

#upload-image-mms, .emoticonSelector, .scheduleSelector {
background-image: none;
background-color: #222;
border: 0px;
padding: 3px !important;
}

#powerViewThreadsHolder .dcsendicon {
background-size: 22px;
background-position: center center;
background-repeat: no-repeat;
background: #222;
}

.receivedText {
background: #222;
border: 1px solid #FF0000;
text-align: left;
}

.threadItem {
box-shadow: inset 0 1px rgba(0, 0, 0, 0.6);
border-radius: 5px;
font-size: 13px;
line-height: 15px;
}

.sentText {
background: #222;
border: 1px solid red;
/* text-align: right; */
box-shadow: inset 0 1px black;
}

#pinnedContent {
background: #5C0B0B;
-webkit-box-shadow: 0 0 5px rgba(51,51,51,0.4);
-moz-box-shadow: 0 0 5px rgba(51,51,51,0.4);
box-shadow: 0 0 5px rgba(51,51,51,0.4);
-webkit-transition: right 200ms ease-out;
-moz-transition: right 200ms ease-out;
-o-transition: right 200ms ease-out;
transition: right 200ms ease-out;
}

#navBarTabs {
background-color: #222;
border-right: 1px solid #222;
}

#threadListHolder {
background-color: #621212;
}

#singleMessageContainer .contentPanelHeader {
background-color: #222;
}

#navBarTabs > .active > a, #navBarTabs > .active > a:hover {
background-color: #222;
color: red;
}

.sentText:after {
border-color: rgba(203, 217, 221, 0);
border-left-color: #FF0000;
border-width: 10px;
}

.sentText:before {
border-color: rgba(203, 217, 221, 0);
border-left-color: #FF0000;
border-width: 10px;
}

.receivedText:after {
border-color: rgba(232, 232, 232, 0);
border-right-color: #FF0000;
border-width: 10px;
}

.receivedText:before {
border-color: rgba(232, 232, 232, 0);
border-right-color: #FF0000;
border-width: 10px;
}
}
