css:
@-moz-document domain("producthunt.com") {
/*******************Producthunt Homepage*****************/

html {
 background-color: #252423 !important; 
}


.header--primary,.header {
  background-color:#252423;
  border-bottom: #252423;
}

/*Search bar*/
.header--search .header--search--wrapper {
	background: #252423 !important;  
}
/*Border around search bar*/   
.header--search .input {
	box-shadow: inset 0 1px 2px 0 #353433, inset 0 0 0 1px #353433 !important;   
}
  
  
/*Change color Today,Yesterday Etc..*/
.posts--date--name {
	color: #DA552F !important;
}
  
  
.header--secondary,.v-links {
	background: #353433;
  	border-bottom:#353433;
}

.header--secondary--link.m-active {
	color: #dc5425 !important;
}


/*Product Name Color === Orange*/
.post-url,.title {
 color: #DA552F !important; 
}

/*Product Containter (Main Page) === slate grey*/
.post {
   background: #353433; 
}

/*Changing Background-Color of Div surrounding search field*/
.header--search--wrapper,.input-wrapper{
  background: #25242 !important;
  }


/*Hide Advertisement like Banner*/ 
.notice {
  display: none;
}

/*Upvote Sqaure Background*/
.upvote {
   background-color:#999999; 
  }

}

@-moz-document domain("producthunt.com") {
/*******************Product Hunt User Profile Page*****************/

html {
 background-color: #252423 !important; 
}

/*Background Div or Hero Behind User*/
.page-header {
	background: #252423 !important;
} 

/*Search field background*/
#search-field {
	background: #353433 !important;
}

/*Navigation containing 4 sections*/
.page-header--navigation {
	background:#353433 !important;
}


.page-header--navigation--tab.m-active a, .page-header--navigation--tab a:active {
        background: #DA552F !important; 
}
}

@-moz-document url("http://www.producthunt.com/posts/oneid") {

/*******************Produchunt Clicking Product Side Bar Appearing *****************/
/*Removes White Transparent Cover where Upvotes are (see left side)*/
.modal {
   background: none; 
  }

/*Changing Background Where product is shown*/
.modal-post--header {
	background: #252423 !important;    
}

/*Changing upvote Div */
.upvote.m-big.m-upvoted {
	background: #999999 !important;    
}

/*Background color of modal*/      
.modal-post {
	background:#353433 !important; 
}

/*Changing background color of modal paragraphs*/
.modal-post--comment--text {
	color: #999999 !important;         
}
        
/*Changing color of text of user who commented*/
.modal-post--comment--name {
	color: #DA552F !important;
}
        
        
/*Background color of user profile on Hover*/
.user-card {
	background: #353433 !important;          
}
      
}

@-moz-document url("http://www.producthunt.com/activities#") {
/*******************Produchunt Activities Page *****************/
html {
 background-color: #252423 !important; 
}


.page-main {
	background:#353433 !important;    
}
  
.activity--headline {
	color: #DA552F !important;  
}
}

@-moz-document url("http://www.producthunt.com/collections") {
/*******************Produchunt Collections Page*****************/

html {
 background-color: #252423 !important; 
}


.featured-collections--title {
    color: #999999;
}


}

@-moz-document url("http://www.producthunt.com/tutorials/collections") {
/*******************Produchunt Tutorials Collections Page*****************/


.fullscreen-overlay {
  background:#353433 !important;
}
  

.p-collections-tutorial--heading {
	color: #DA552F !important;      
}


.p-collections-tutorial--text {
  color: #fff !important;
}
    
/*Back Button Color*/
.button.v-grey {
	background: #00B27F !important;
    border-color: transparent;
}

}

@-moz-document url("http://www.producthunt.com/my/invites") {
/*******************Produchunt Invites Page*****************/


.modal--header--title {
  color:#DA552F !important;
}
  
.modal--header {
	background: #252423 !important;  
}

.modal--content {
   background:#353433 !important; 
  }
      
.invite--form-input {
	background: #252423 !important;      
}
}

@-moz-document url("http://www.producthunt.com/v1/oauth/applications") {
/*******************Produchunt 1st Applications Page*****************/
h1 {
 color: #DA552F !important; 
}

p {
  color: #ABA3A3 !important;
}
}

@-moz-document url("http://www.producthunt.com/v1/oauth/applications/new") {
/*******************Produchunt Add Application Page..(Create a new Appe*****************/
