css:
/* Einfache Liste für Neue Fragen bei gutefrage.net*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.gutefrage.net/fragen/neue/") {
  
#header, .created {
  display: none !important;
}
  
  
.text, .themes, .tag, .text, .replies::after {
  display: none !important;
}
  
.info {
  font-size: 0.5em;
  line-height: 1em;  
}
.info > dt{
  display: none !important;
}
.replies{
  float: right !important;
  margin-top: -25px;
  margin-bottom: 25px;
  font-size: 1.5em !important;
}
  
#page, #content, .block, .header {
  padding: 0px 0px !important;
  margin: 0px 00px !important;
}
.detailLink {
  font-size: 1em;
  line-height: 1em;
}
.entityList{
  width: 620px;
}  
.entityList > li{
  margin: 0px 0px;
}
.diggIt{
  margin: -0px 15px 0px 15px;
}  
}
