css:
/*======================================
  toyama autobi club liquid compact 2015/03
  @-moz-document domain("sone2000gt.blog75.fc2.com")
  ======================================*/
@-moz-document domain("sone2000gt.blog75.fc2.com") {
/*--- unwanteds ---*/
body>iframe,
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: medium !important; font-weight: normal !important;
 line-height: 1.4em !important;
 }
#container {width: auto !important;}

/*--- header ---*/

/*--- footer ---*/

/*--- main contents ---*/
#rwap, #sidemenu2, #main, #sidemenu1 {position: relative !important;}
#wrap {width: 100% !important; margin: 0 -150pt 0 0 !important;}
#main {width: 100% !important; margin: 0 150pt 0 -300pt !important;}
#main>* {width: auto !important; margin: 0 0 0 300pt !important;}
#sidemenu2, #sidemenu1 {width: 150pt !important;}
#sidemenu2 *, #sidemenu1 * {max-width: 150pt !important;}

.entry_body {text-indent: 1em;}
.entry_body br+br {display: none !important;}
.entry_body a, .entry_body img {display: block !important;}

}
