css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
  
  #main-header, #left-sidebar > * {
    background:#383D3B !important;    
    color:#fafafa !important;
    font-family: Gill Sans,Gill Sans MT,Calibri,sans-serif !important; 
    font-size: 12px !important;
    border-radius:0% !important;
    text-decoration:none !important;
    font-weight:100 !important;
    border-top:#000 !important;
    text-transform:uppercase !important;
    border-radius:3px !important;
    box-shadow: 0px 0px 2px rgba(0,0,0, 0.2) !important;
  }
  
  body{
    font-family: Gill Sans,Gill Sans MT,Calibri,sans-serif !important;
    font-variant:all-small-caps !important;
    font-size:14px !important;
    background-color:#fafafa !important;
    max-width:100% !important;
    text-shadow: 0px 0px 1px rgba(0,0,0, 0.1) !important;
    
  }

  a {
    color:#4392F1 !important;
    text-decoration:none !important;
    text-transform:Capitalize !important;
    font-weight:200 !important;
    font-kerning:auto !important;
  }
  
  a:visited{
    color:#7FBEEB !important;
    text-shadow: 0px 0px 1px !important;
  }
  
  #simple-search > *, #advanced-search select, #advanced-search-submit > button:nth-child(1), #advanced-search-submit > input:nth-child(2){
    background:#7FBEEB !important;
    border:1px solid rgba(100,100,100, 0.2)  !important;
    margin-bottom:10px !important;
    border-radius:1px !important;
    padding:2px !important;
    text-transform:capitalize !important;
  }
  
  #search-submit{
    max-height: 22px !important;
}
  
  #left-sidebar .ad {
    display:none !important;
  }
  
  body > footer{
    padding-left:200px !important;
    padding-top:250px !important;
  }
}
