css:
@-moz-document domain("anime-pictures.net") {

body,b,a.big_tag {font-size:10pt}
.title,.sidebar_block h2 {font-size:10pt !important}
h1 {font-size:16pt;line-height:16pt}
input,select,.tags li a:not(.big_tag) {font-size:9pt}
.post_content,.sidebar_block,#search_form,#message_form,ul.body li,#chat_users {font-size:10pt;line-height:14pt}
.post_content_avatar {min-height:130px}
.post_content .body {padding:5px 0 5px 5px}
.menu {font-size:9pt;line-height:32px}
.menu select {font-size:7pt}
.menu,.menu ul li,.menu ul li a {height:32px}
.menu ul li {padding:0 8px}
.icon_add_image,.icon_new_message,.icon_new_image,.icon_delete_big,.icon_edit_big {width:24px;height:24px}
.form_table .icon_delete_big,.form_table .icon_edit_big,.icon_delete,.icon_frame,.icon_edit {height:16px;width:16px}
#chat_field tr td {padding:0 2px}
}
