@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.spi0n.com") {

  #sidebar {
    display: none;
  }
  #middle.white_ver .middle_line.sidebar_bg {
    background: none;
  }
  #content {
    width: auto;
  }
  .entry iframe {
    display: inherit;
    text-align: center;
    width: 100%;
}
  #middle img {
    height: 50%;
    margin: 0 auto;
  }
  
  img.noimgbg, #middle img.noimgbg {
    display: none;
}
  span.image_link_hover {
    display: none;
  }
}
