css:
/* This theme is based on "FurAffinity - Grey Rust"(https://userstyles.org/styles/98676/furaffinity-grey-rust) by Artex(http://www.furaffinity.net/user/artex./) */
/* It is inspired by Persona (Inspired doesn't mean "it's a rip of the style of"...). */
/* Hope you like it.~ */
/* My profile: http://www.furaffinity.net/user/cuteness-overlord */

/*
#D8C0B6 - background
#E9DBCC - content
#0480A4 - bars, titles
#1A1A1A - dark selection, borders, seperators.
#D3A594 - visited links
#97574E - colored highlights
*/

@-moz-document domain('www.furaffinity.net'), domain('sfw.furaffinity.net') {
/*fix for background on particularly stretchy images*/
    :root{
        background-color:#D8C0B6 !important;
    }
/*fix navigation bar to top*/
    .block-menu-top {
        z-index: 28;
        top:0px !important;
        position: fixed;
    }
    .block-menu-top a{
        color:#E9DBCC !important;
    }
   .block-menu-top a[href^="/user/"]{
        color:#FFFFFF !important;
    }
    .block-menu-top a[href^="/logout/"]:hover{
        color: red !important;
    }
    .block-menu-top td{
        background-color:#0480A4 !important;
        box-shadow: 0px 2px 0px #443533;
        opacity:0.9;
    }
/*move banners downwards to fix overlapping*/
    .block-banners{
      /*top:35px;*/
      /*height:250px;*/
    }
    block-banners.clearfix{
        height: 230px !important;
    }
    .block-banners div.ads{
        left: auto !important;
        position: absolute !important;
        right: 10px !important;
        z-index: 15 !important;
    top:50px;
    }
    #fa_header{
       width: 600px !important;
       height: 200px !important;
       left:1.5%;
       top:10%;
       position:relative !important;
       background: url("http://puu.sh/grHMO/66de11c55f.png") no-repeat scroll 0% !important;
       background-color: rgba(0,0,0,0.0) !important;
       z-index: 10 !important;
    }
    ul.dropdown ul a:hover{
        background-color:#97574E !important;
    }

    li#sfw-toggle.active {
        background-color: #97574E !important;
    }
    #news{
        margin:45px auto 0 !important;
        border-radius:0 !important;
        border:none !important;
        width:97.5% !important;
        background-color:#97574E !important;
    }
    #news img.dismiss {
        content: url("http://i.imgur.com/tj8aaDc.png") !important;
        margin: 8px !important;
    }

    /*recolor everything...*/
    #gallery .cat > .maintable > tbody > tr:nth-child(1) > td,
    #scraps .cat > .maintable > tbody > tr:nth-child(1) > td,
    #favorites .cat > .maintable > tbody > tr:nth-child(1) > td
    {
      background-color: #0480A4!important;
    }
    /*Who needs unique identifiers?*/
    .innertable > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1){
        background-color: #0480A4 !important;
    }
    #gallery .cat > .maintable,
    #scraps .cat > .maintable,
    #favorites .cat > .maintable
    {
      border-collapse: collapse;
    }
/*Improve readability of text submissions*/
    #submission table.maintable:nth-child(2) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1){
        color:black !important;
        background-color:white !important;
        padding:20px !important;
        width: 50% !important;
    }
    #submission table.maintable:nth-child(2){
     max-width:800px !important;
    }
    #submission table.maintable:nth-child(2) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) a{
     color:rgb(0,255,240) !important;
    }
/*-------------------------------------*/
    .innertable{
        background-color:#D8C0B6 !important;
    }
    body > div {
        background: url(http://puu.sh/grHHC/c7e12d2aaf.png) !important;
    }
    .maintable{
        background-color:#0480A4 !important;
        color: #D8C0B6;
    /*border-collapse: collapse;*/
    }
    .cat{
    background-color:#0480A4 !important;
    border:none !important;
    }
    .alt1{
        background-color:#E9DBCC !important;
    }
   div#messagecenter-other a:visited,
  .auto_link a:visited,
        .auto_link:visited,
  .alt1 a:visited {
    color: #7E625E !important;
  }
  .alt2{
      background-color:#E9DBCC !important;
  }
/*no more rounded edges*/
  center.flow s > a, center.flow img{
      border-radius: 0 !important;
  }
  center.flow.userpage-submissions b.active a, center.flow.userpage-favorites b.active a {
    border: none !important;
  }
  .tab a{
      background-color:#E9DBCC !important;
  border:none !important;
  }
  .tab u{
    background-color: #0480A4 !important;
    border: none !important;
  }
  .container-comment{
      background-color:#E9DBCC !important;
  border: none !important;
  }
  .container-comment TH{
      background-color:#0480A4 !important;

  }
/*fade in the description popup*/
  #description_popup{
      animation:fadein 0.5s;
      -webkit-animation:fadein 0.5s;
  }
  @keyframes fadein{
      from {opacity:0;}
      to {opacity:1;}
  }
  @-webkit-keyframes fadein{
      from {opacity:0;}
      to {opacity:1;}
  }
  #description_popup:first-of-type{
      opacity:0;
  }
/*fancy hover transitions owo*/
  a[href^="/watch/"] font:hover{
      color:gold !important;
  }
  .tab a{
      height:20px !important;
      transition: padding 0.2s;
  }
  .tab a:hover{
      padding-top:8px !important;
      color:#999999 !important;
  }
  .r-general,
  .r-mature,
  .r-adult{
      overflow:visible !important;
  }
  .r-general a,
  .r-mature a,
  .r-adult a{
      transition: transform 0.1s;
      border:none !important;
      padding: 0 !important;
      background: none !important;
  }
  .r-general a:hover,
  .r-mature a:hover,
  .r-adult a:hover{
      transform:scale(1.05,1.05);
      -webkit-transform:scale(1.05,1.05);
  }
  /*random decoravitve border and background to submissions*/
  #submissionImg{
     border: 3px #D8C0B6 !important;
     background:#D8C0B6 !important;
  }
  .menu li ul li:hover{ /*drop down menu on usernames*/
      background-color: #97574E !important;
  }
  .menu li ul li{
      background-color:#0480A4 !important;
      border:1px solid #D8C0B6 !important;
  }
  /*checked submissions*/
  center.flow.messagecenter b.checked{
      background-color:#0480A4 !important;
      border-radius: 0 !important;
  }
  /*shouts*/
  .lead{
      background-color: #0480A4;
      height: 20px;
  border:none !important;
  }
  .message-stream li{
    transition: background-color 0.2s;
  }
  .message-stream li:hover{
    background-color:#E9DBCC !important;
  }
  .message-stream li.checked{
    background-color:#1A1A1A !important;
  }
  .section-controls{
    border-bottom:1px solid #111111 !important;
  }
  div#messagecenter-submissions h3.date-divider{
    border-bottom:1px dotted #111111 !important;
  }
  .listbox{
    background-color: #E9DBCC !important;
    border-color: #D8C0B6 !important;
    color: #7E625E !important;
  }
  .container-comment TD.cat A{
    padding: 2px 5px 0 5px !important;
    background-color: #97574E !important;
  }
  TABLE, TR, TD, TH{
    font-size: 7.5pt !important;
    text-shadow: 0px 0px 3px rgba(233,219,204,1.00), 1px 1px 1px #443533;
  }
  #admin_notice_do_not_adblock TD{
    background-color: #E9DBCC !important;
    position: relative;
  }
  #admin_notice_do_not_adblock{
    z-index:50 !important;
    top: 10px !important;
    position:relative !important;
  }
  #page-commission-info .types-table tr.even th, #page-commission-info .types-table tr.even td {
    background-color: #D8C0B6 !important;
  }
  #page-commission-info .types-table .info dt.open {
    color: #8AB171 !important;
  }
  fieldset {
    border: 1px solid #D8C0B6 !important;
  }
  /*match image button colors on message forms to gray theme*/
  #MsgForm .hand,
  #add_comment_form .hand {
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
  }
  /*fix background on compose button in note center*/
  #pms-form > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(4) {
    background: transparent !important;
  }
  /*note center update patch*/
  .note-view-container{
      border: none !important;   
  }
  #notespage .note-list-container {
      border: none !important;
  }
  #notespage .selected-view td, #sessions-form table.container tr.checked td, #activity-periods-list table.container tr.checked td, .personal-messages tr.checked td {
      Background-color: #97574E !important;
  }
  /*patch highlighted stream journals*/
  .stream-notification a{ /*aye, something that makes sense*/
     color: rgb(249, 174, 61) !important;
  }
  .stream-notification {
     background-color: #D8C0B6 !important;
  }
  .bbcode_quote {
      background-color: #D8C0B6 !important;
      border: 5px double #1398A9 !important;
  }
  /*Better Horizontal Lines*/
  .bbcode_hr {
    border: 0;
    height: 2px;
    background-image: -moz-linear-gradient(left, rgba(19,152,169,0), rgba(19,152,169,0.75), rgba(19,152,169,0));
  }
  /*Smaller Sub/Sup Font Size*/
  .bbcode_sub, .bbcode_sup {
    font-size: 6.5pt !important;
    font-weight: lighter;
  }
  /* Submissions/Favorites frame */
  center.flow.userpage-submissions.twolines, center.flow.userpage-favorites.twolines {
      background-color: #D8C0B6 !important;
      border-top: 5px double #1398A9 !important;
      border-bottom: 5px double #1398A9 !important;
      border-left: 1px solid #1398A9 !important;
      border-right: 1px solid #1398A9 !important;
  }
  .footer, .ads, .ldot, .bdot {
      color: #7E625E !important;
  }
  .alt1{
      color: #046CA5 !important;
  }
  .tab u {
      color: white !important;
  }
  .footer A:link {
      color: #443533 !important;
  }
  .footer A:hover {
      color: #046CA5 !important;
  }
  .alt1 A:link {
      color: #443533 !important;
  }
  .alt1 A:hover {
      color: #046CA5 !important;
  }
  .lead A:link {
      color: white !important;
  }
  .lead A:hover {
      color: #443533 !important;
  }
  ul.dropdown ul {
      background-color: #443533 ! important;
  }
  ul.dropdown ul a:hover{
      background-color: #046CA5 !important;
  }
  .container-comment TD, .container-comment TH {
      color: #046CA5 !important;
  }
  .footer {
      background: url(http://puu.sh/grHHC/c7e12d2aaf.png) !important;
  }
  /*Trying to fix messages */
  div#messagecenter-other fieldset h3 {
      background-color: #046CA5;
      color: white;
  }
  div#messagecenter-other {
      background-color: #E9DBCC !important;
      border: 5px double #1398A9 !important;
  }
  div#messagecenter-other fieldset {
      color: #046CA5;
  }
}
