.lsidebar { /*Animelista*/
	float: none;
	margin-top: 130px;
	margin-right:0px;
	margin-bottom:0px;
	margin-left:955px;
    width: 30px;
  	height: 200px;
    overflow-y: hidden;
  	overflow-x: hidden;
  	position: absolute;
    z-index: 9999999;
    background-color: #F6FFF6;
  	box-shadow: 4px 0px 10px yellow;
    border-radius: 10px;
  	border: 1px solid green;
  	border-left: 0px;
  	border-top-left-radius: 0px;
  	border-bottom-left-radius: 2px;
    opacity: 1;
  	text-align: center;
  	display: hidden;
 -webkit-transition: 0.2s ease-in-out;
    -moz-transition: 0.2s ease-in-out;
    -o-transition: 0.2s ease-in-out;
    transition: 0.2s ease-in-out;

	}

.lsidebar:after {
    	content: "Episodes";
      	overflow: hidden;
  		font-family: Arial, Helvetica, sans-serif;
  		font-size: 150%;
  		font-weight: 900;
  		height: 20px;
 		display: block;
  		position: absolute;
  		top: 60px;
  		right: -30px;
  		margin-right: 0px;
		-webkit-transform: rotate(90deg);
  		-moz-transform: rotate(90deg);
  		-o-transform: rotate(90deg);
  		-ms-transform: rotate(90deg);
  		transform: rotate(90deg);
}

