css:
@-moz-document url-prefix("https://www.youtube.com/watch?") {
   .player-width {
   	width: 142vh
   }
   .player-height {
   	height: 80vh;
   }
   #player {
   	margin-bottom: -95px !important;
   	margin-left: 100px !important;
   }
   #watch-appbar-playlist {
   	opacity: 0;
   	position: relative;
   	width: 10vh;
   	margin-left: 14vh !important;
   }
   html,
   body,
   #player,
   #player-legacy {
   	background: #191919 !important
   }
   #watch-appbar-playlist:hover {
   	position: relative;
   	width: 40vh;
   	margin-left: 14vh !important;
   	-webkit-animation-name: fadeInLeft;
   	-webkit-animation-duration: 1s;
   	animation-duration: 1s;
   	-webkit-animation-fill-mode: both;
   	animation-fill-mode: both;
   	animation-name: fadeInLeft;
   	animation-delay: 0s;
   }
   @keyframes fadeInLeft {
   	0% {
   		opacity: 0;
   		-webkit-transform: translate3d(-80%, 0, 0);
   		transform: translate3d(-0%, 0, 0);
   	}
   	100% {
   		opacity: 1;
   		-webkit-transform: none;
   		transform: none;
   	}
   }
   .fadeInLeft {
   	-webkit-animation-name: fadeInLeft;
   	animation-name: fadeInLeft;
   }
   #movie_player {
   	box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.7) !important;
   }
   #player-mole-container {
   	margin-left: -80px;
   	max-width: 1460px;
   	z-index: 1 !important;
   	margin-bottom: 00px !important;
   }
   #content {
   	margin-top: 40px
   }
   #watch7-headline h1 {
   	text-align: center;
   	width: 100vh;
   	opacity: 0.7
   }
   #watch7-headline h1:hover {
   	opacity: 1
   }
   #watch7-main {
   	position: relative;
   	margin-top: 0px;
   	margin-bottom: 30px !important;
   	z-index: 0;
   	width: 134% !important;
   }
   #watch7-main-container {
   	width: 65%;
   }
   #watch7-sidebar-discussion {
   	margin-left: 0px !important;
   	width: 387px !important;
   }
   .watch #watch7-content {
   	position: relative;
   	z-index: 0 !important
   }
   .watch #content.yt-card {
   	position: relative;
   	z-index: 0 !important;
   }
   .watch #watch7-sidebar-contents {
   	padding-left: 00px
   }
   .watch #watch8-secondary-actions.watch-secondary-actions.yt-uix-button-group {
   	margin-left: 101px !important;
   }
   .watch #watch7-content.watch-content {
   	width: 1079px!important;
   	margin-left: 73px;
   	display: flex;
   	justify-content: center;
   }
   .watch #watch7-sidebar {
margin-top:-400px!important;
    padding-left: 12%;
   	width: 22.8%!important;
   	 	
	transition: all .2s;
   }
  
   .watch.watch-non-stage-mode #player.content-alignment {
   	justify-content: center!important;
	transition: all .2s;
   }
   .watch.watch-non-stage-mode #player.content-alignment {
   	justify-content: center!important;
   transition: all .2s;}
   .autoplay-bar {
   	display: none;
   }
   video.video-stream.html5-main-video {
   	width: 100% !important;
   	left: 0px;
   	top: 0px;
   	transform: none;
   transition: all .2s;}

}
