@-moz-document url-prefix("https://www.tumblr.com/"), url-prefix("http://www.tumblr.com/") {
/* Code by rootehful.tumblr.com originally, edited by cthuulu.tumblr.com - v1.7 - Send an ask for problems and requests. Check https://userstyles.org/users/276987 for updates and new versions.*/

.l-header .selection_nipple {
border-bottom: 0px solid #191919 !important;									/* Xkit-Fix Remove border under blog icons*/
}

.identity, .identity-refresh {
  					background-color: #000000;									/*background color*/
  } 

.identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content {
          			background-color: rgba(0,0,0,0.1);							/*Center background color*/
}

.identity-refresh .l-container.l-container--two-column-dashboard .left_column {
  					background-color: transparent;								/*Post background color*/
}
        
#right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column {
  					background-color: transparent;								/*Sidebar background color*/
  					margin-top: 10px;											/*Sidebar Position*/
}
  
.identity-refresh .plexi.show.color {
					background: rgba(15,15,15,.85);								/*New post/Reblog Window Background Color*/
}

#dashboard_controls {
  					margin-top: 16px;											/*Page Nav Position*/
}

#dashboard_controls .control {
  					color: rgba(255,255,255,0.5);								/*Page Nav Color*/
					text-decoration: none;
					text-shadow: transparent;
}

.l-header-container--refresh {
    				background-color: rgba(0,0,0,0.4);							/*Header bar background color*/
  }

.compose-button {
    				border-color: transparent;			
					background: rgba(255,255,255,0.5);							/*Button background color*/
  					-moz-border-radius: 5px;
					border-radius: 5px;
  }

.xtimestamp {
  					margin-left: 20px;											/*Xkit Timestamp Align*/
}



#new_post_in_tracked_tags_bubble.search_query_btn.pill {
  					margin-top: -26px;											/*NEW tracked tags button*/
}

.identity-refresh .post.post_full {
  					border-radius: 6px;											/*Rounded Edge Posts*/
  }

.identity-refresh .post_full .post_permalink {
        			border-color: #0F0F0F #0F0F0F transparent transparent;		/*Permalink background color*/
 }

.post_full .post_wrapper {
  					background-image: url(https://31.media.tumblr.com/fa50f2c239235a2a09fc1c244a9608d0/tumblr_inline_ne9sp8CY111qgprmm.png);
					background-repeat: no-repeat;								/*Post Speech Bubble Edge*/
					background-position-x: 14px;
					background-position-y: 8px;
					margin-left: -20px;
					padding-left: 20px;
 }


.identity-refresh .right_column:after {
        			background: transparent;									/*Delete Sidebar Gradient*/
      }
      
      
.identity-refresh .controls_section.user_list li .follow_list_item_blog:before {
  					background-image: none;										/*Delete Recommended Blog Gradient*/
					border-right: 0px;
}

.identity-refresh .radar .radar_footer .radar_avatar:before, .radar .radar_footer .radar_avatar:before {
  					background-image: none;										/*Delete Radar Gradient*/	
          			background: transparent;	
        }
      
.l-header .selection_nipple {
            		border-bottom: 12px solid #6C9526;							/*Blog Selection Nipple*/
 }
        
.identity-refresh .right_column .follow {
                	color: rgba(255,255,255,0.5);								/*Recommended Follow Button*/
 }

.ui_search.tab.active .search_query, .ui_search.tab.active .search_form_field:before {
                    color: rgba(255,255,255,0.5);								/*Search bar border less intense*/
                    border-color: rgba(255,255,255,0.5);
}
}
