css:
@-moz-document domain("kaskus.co.id") {

/*
 *
 * @author      Cinhau
 * @version     1.0.0
 *
 */

  /*Hide unnecessary frame*/
  .bottom-frame, .kasad-h, #c-thread, #sell, .banner-top-ads, .col-sm-3.sidebar, .row.sidebar-wrap, .related-thread.nongol, .popover.fade.bottom.in, .kaskus-ads.postlist, .row.ads-lb-wrap, .col-sm-12.banner-frame, .user-control-stick, #thread_post_list > .row > .col-xs-12 > .text-center, .skin, #feedback, .kaskus-ads .postlist, .site-footer {
    display:none;
  }
  
  /*Post*/
  .listing-wrapper{
    width: 97%;
    position: absolute;
    left: 20px;
    margin-top: 20px;
  }
  .main-content {
    width: 100%;
    margin-left: auto;
    margin-right: auto;
    float: none;
  }
  .quick-navigation {
    display:none;
  }
  .postlist {
    margin-bottom: 0px;
    border: none;
  } 
  .postlist .author {
    border: none;
  } 
  .col-xs-12 {
    min-height: 0px;
    position: relative;
    left: 0px;
  }
  
  /*Profile post*/
  .user-avatar{
    height: auto;
    width: auto;   
  } 
  .postlist .author .photo {
    max-height: 110px;
    max-width: 110px;
    height: auto;
    width: auto;
  } 
  .user-info > div {
    display: block !important;
  }
  .user-info { 
    font-size: 0px; 
  }
  .user-info > .title, .user-info > .meta { 
    font-size: 12px; 
    float: left; 
  }
  .user-info > .meta { 
    clear: both; 
  }
  
  body.response #thread_post_list .row.nor-post .postlist .author {
    width: 140px;
  }
  body.response #thread_post_list .row.nor-post .postlist .author .photo {
    max-width: 117px;
    max-height: 117px;
  }
  body.response #thread_post_list .row.nor-post .postlist .user-avatar {
    width: auto;
    height: auto;
  }  
 .user-avatar {
   border: 1px solid #FFF;
  }
  
  /*Header bar*/
  .site-header {
    opacity: 0.8;
  }
  footer.site-footer {
    opacity: 0.8;
  }
  
  /*background image*/
  /*body{
    background: url('http://www.example.com/sample.jpg') no-repeat fixed center;
    margin:0;
    overflow-x:hidden;
  }*/
  
  /*Font*/
  .postlist .entry, span {
    font-size: 12px;
  }  
  .title, .userid, .user-info > .meta, .postlist .entry, .entry-date, .description, .entry-post > .entry-content, .polling-wrapper, .listing-wrapper-user-content, .group-desc {
    color: #484848;
  }
  
  /*Fixed some design*/
  .head-title, .highlights .hot-thread ul.list-top-thread, .main-bar .two-col > div ul, .regional .top-location ul li a, .uniqness, #refine-search .categories-wrap, .message-page .message-con .list-message{
    background:#f7f7f7;
  }
  .highlights .hot-thread ul.list-top-thread, .uniqness{
    margin: 0px;
  }
}
