css:
@-moz-document domain("www.weibo.com") , domain("weibo.com"){

/*Weibo.css - https://github.com/s0s0/BrainFlower
Licensed under the WTFLP license - http://www.wtfpl.net/txt/copying/
Copyright (c) 2015 Qi Li*/

/*OSX 上为冬青黑、Windows 上为雅黑、Linux 上为文泉驿微米黑，英文为 Helvetica Neue 或 Arial*/

body {
    font-family: 'Helvetica Neue',Arial,'Hiragino Sans GB','Microsoft YaHei','WenQuanYi Micro Hei',sans-serif;
    color: #494A4C;
    background: #fff;
}

/*播放器、私信聊天窗口、发送微博框下部广告、会员专区、右侧红包、发微博框顶部、热门话题介绍、热门搜索、首页更新圆点、微博转发聚合、右侧模块的更多、右侧广告*/

.PCD_mplayer, #WB_webim, #v6_pl_content_biztips, #v6_trustPagelet_recom_member, #v6_pl_rightmod_hongbaoentrance, .title_area, a.W_icon.icon_setskin, .WB_right_expand, .gn_topmenulist_search, .W_new, a.W_ficon.ficon_more.S_ficon, .WB_feed_together,.WB_search_s .pos, a.WB_cardmore.S_txt1.S_line1, div[id^="v6_pl_rightmod_ads"],div[feedtype=ad] {
    display: none;
}

/*右侧广告、排行榜、电影榜*/

div#v6_pl_rightmod_recominfo > div:nth-child(4), div#v6_pl_rightmod_recominfo > div:nth-child(5), div#v6_pl_rightmod_recominfo > div:nth-child(6), div#v6_pl_rightmod_recominfo > div:nth-child(7) {
    display: none;
}

/*右侧浮动*/

.WB_main_r > div:nth-child(3) {
    display: none;
}

/*右侧头像 cover*/

div#skin_cover_s {
    background: none
;
}

img.W_face_radius {
    width: 50px;
    height: 50px;
}

.headpic {
    width: 50px;
    height: 50px;
    padding: 0;
    bottom: 0;
    left: 0;
    margin-left: 0;
    position: static;
}

.W_person_info {
    height: 127px;
}

.W_person_info .cover {
    height: 0;
}

.W_person_info .nameBox .W_icon_level {
    display: none;
}

.nameBox {
    height: 0;
    padding-top: 0;
    margin-left: 62px;
    text-align: left;
}

.user_atten {
    height: 44px;
    margin: 65px 0 0 0;
}

.user_atten li .S_txt2 {
    font-size: 14px;
    color: #999999;
    margin-top: 10px;
}

.user_atten li a:hover {
    color: #EB7350;
}

.user_atten li strong {
    color: #494A4C;
}

.user_atten > li:nth-child(2),.user_atten > li:nth-child(3) {
    padding-left: 30px;
}

.user_atten li {
    text-align: left;
}

.W_person_info .WB_innerwrap {
    padding: 4px 0px 0px 0px;
}

/*右侧其他区域*/

.WB_innerwrap {
    padding: 6px 0;
}

.WB_cardtitle_a, .WB_cardtitle_b {
    padding: 0;
}

.WB_cardtitle_a .opt_box, .WB_cardtitle_b .opt_box {
    right: 0;
}

.WB_cardtitle_b {
    border: 0;
}

.WB_right_module .hot_topic li {
    padding: 10px 0 5px;
}

.WB_right_module .hot_topic li .total {
    color: #999;
}

em.W_ficon.ficon_rotate.S_ficon, .WB_cardtitle_a .opt_change, .WB_cardtitle_b .opt_change {
    color: #999;
}

/*导航栏顶部色条*/

.WB_global_nav {
    border-top: 0;
    height: 66px;
    background: #F8F8F8;
    box-shadow: none;
    border-bottom: 1px solid #eee;
}

/*移除顶部导航栏的响应式布局*/

@media screen and (min-width: 1295px) {
  .WB_global_nav .gn_header {    
    width: 1100px;
  }
  .WB_global_nav .gn_logo .box .logo {    
    background-position: 0% 40%;
  }
  .WB_global_nav .gn_search {    
    margin: 4px 0 0 0;
  }
  .WB_global_nav .gn_search .ficon_search {    
    top: 8px;
  }
  .WB_global_nav .gn_search .W_input {    
    width: 339px;    
    margin-left: 10px;    
    height: 40px;    
    background: #FFF;    
    border: 1px solid #D1D0D0;    -webkit-border-radius: 4px;
    border-radius: 4px;    
    padding: 0px 0px 0px 10px;    
    font-size: 14px;    
    color: #BBB;
  }
  .WB_global_nav .gn_topmenulist_tips {    
    right: -38px;    
    top: 44px;    -webkit-box-shadow: none;
    box-shadow: none;    
    color: #666;
  }
  .WB_global_nav .gn_topmenulist_tips ul li {    
    padding: 9px 30px 9px 11px;
  }
  .WB_global_nav .gn_topmenulist_tips .ficon_close {    
    font-size: 15px;    
    color: #999;
  }
}

/*中右栏宽度*/

div#plc_main {
    width: 950px;
}

.WB_main_c {
    margin-right: 50px;
}

.WB_main_r {
    width: 300px;
    margin-right: 0;
}

/* POP box 顶部色条*/

.content {
    border-top: 2px solid #fff;
}

/*移除更多操作（屏蔽啥的）*/

.screen_box {
    display: none;
}

/*移除微博来源*/

.WB_from.S_txt2 > a:nth-child(2) {
    display: none;
}

/*移除各种 card*/

.WB_feed_spec.S_bg2.S_line1 {
    display: none;
}

/*移除各种背景*/

.WB_frame {
    background-color: transparent;
    width: 1100px;
}

/*修改 header*/

.WB_miniblog {
    background: none;
    padding-top: 90px;
}

.WB_global_nav .gn_header {
    height: 66px;
    margin: 10px auto;
}

.WB_global_nav .gn_nav_list li a {
    font-size: 15px;
}

.WB_global_nav_alpha {
    box-shadow: none;
    background: #F8F8F8;
}

.WB_left_nav.WB_left_nav_Atest.autoFixed {
    display: none;
}

.gn_nav_list > li:nth-child(1) {
    display: none;
}

/*微博类型 icon 颜色*/

em.W_ficon {
    color: #494A4C;
}

/*修改左侧栏 nav 颜色、字号、icon、padding*/

.WB_left_nav .S_txt1 {
    color: #494A4C;
}

.WB_left_nav .lev .ico_block {
    display: none;
}

.WB_left_nav .lev a, .WB_left_nav .lev_Box h3.lev a {
    padding: 0;
    font-size: 16px;
}

.WB_left_nav .lev_line {
    display: none;
}

.WB_left_nav.WB_left_nav_Atest > div:nth-child(3), .WB_left_nav.WB_left_nav_Atest > div:nth-child(4) {
    display: none;
}

span.levtxt:hover {
    color: #EB7350;
}

/*移除 card 边框并对齐*/

.WB_cardwrap {
    box-shadow: none;
    border-radius: 0;
    margin: 0;
    border-bottom: 1px #eee solid;
    padding: 10px 0;
}

.WB_feed_detail {
    padding: 10px 0 0 0;
}

/*feed card title、背景*/

.WB_feed .WB_cardtitle_b {
    display: none;
}

.WB_expand.S_bg1 {
    background: #f5f5f5;
    margin: 0;
    padding: 8px 14px;
}

/*微博单条布局*/

.WB_text.W_f14 {
    margin-top: 14px;
    position: relative;
}

/*微博图片区域修改*/

.WB_media_wrap {
    margin: 6px 0 8px -10px;
}

/*阅读进度*/

.WB_cardtitle_a.W_tc {
    color: #999;
}

/*feed 推荐关注*/

.WB_feed > div[node-type=feed_spread] {
    display: none;
}

/*更新提示*/

div#home_new_feed_tip {
    padding: 4px;
    border: 1px #ffeebb solid;
    background: #FEF9DF;
    border-radius: 4px;
    margin-top: 10px;
}

.WB_notes a, .WB_notes .wrap {
    color: #eb7350;
}

.WB_feed > div[requesttype=newFeed] {
    padding: 4px;
    border: 1px #ffeebb solid;
    background: #FEF9DF;
    border-radius: 4px;
    margin-top: 10px;
}

/*微博底部操作*/

span.line.S_line1 {
    border: 0;
    color: #BBBBBB;
    font-size: 14px;
}

span.line.S_line1:hover {
    color: #eb7350;
}

.WB_row_line a {
    text-align: left;
}

.WB_row_r4 li {
    margin: 0 35px 0 0px;
    width: auto;
}

.WB_feed_handle .WB_row_line {
    border: 0;
}

/*feed 评论框*/

.WB_feed_repeat .WB_repeat {
    border: 1px #DDD solid;
    border-radius: 4px;
    background: #F5F5F5;
}

.WB_feed_repeat .W_tips {
    display: none;
}

.WB_feed_publish > .WB_face {
    display: none;
}

.WB_feed_repeat .WB_publish {
    margin: 0;
}

.WB_feed_repeat .repeat_list {
    padding: 0 20px;
}

.WB_feed_repeat .repeat_list .list_li {
    padding: 7px 0 7px;
}

.WB_feed_repeat .repeat_list .list_box {
    margin: 0;
}

.WB_feed_repeat .WB_face img {
    border-radius: 2px;
}

.WB_publish .p_input .W_input {
    border-radius: 4px;
    box-shadow: none;
    padding: 8px;
    width: 97%;
    font-size: 14px;
}

/*移除 feed 顶部搜索边框*/

.WB_tab_a {
    box-shadow: none;
    margin: 0;
}

/*修改 tab 左边距、颜色、下边框、下边距*/

.WB_tab_a .tab .t {
    padding: 0;
    color: #A9A9A9;
    font-size: 16px;
}

.WB_tab_a .tab li.S_bg2, .WB_tab_a .tab li.curr .b .b1 em i {
    box-shadow: none;
}

.WB_feed {
    margin-top: 10px;
}

.WB_tab_a .tab_box .fr_box {
    box-shadow: none;
    height: 38px;
}

.WB_tab_a .WB_search_s {
    margin: 0;
}

.WB_search_s .W_input {
    padding: 0 0 0 10px;
    width: 155px;
    height: 30px;
    font-size: 13px;
    border-radius: 3px;
    border: 1px solid #D1D0D0;
    color: #BBBBBB;
    box-shadow: inset 0px 1px 1px 0px rgba(0,0,0,0.06);
}

div#v6_pl_content_homefeed {
    margin-top: 30px;
}

/*移除发微博外框*/

.send_weibo {
    border: 1px #DDD solid;
    box-shadow: none;
    margin: 0;
    border-radius: 4px;
    padding: 0;
    height: 156px;
}

.send_weibo .input {
    margin: 0;
    box-shadow: none;
    border-color: #FFF;
    height: 86px;
    padding: 8px;
}

.send_weibo .input .W_input {
    height: 86px;
}

textarea.W_input {
    font-size: 15px;
    line-height: 22px;
}

.func_area {
    padding: 6px 9px;
    border-top: 1px #DDD solid;
}

/*修改头像尺寸*/

.WB_face {
    width: 30px;
    margin-right: 12px;
}

.WB_face .face img {
    width: 30px;
    height: 30px;
}

.WB_detail {
    margin-left: 0px;
    width: 100%;
}

.W_face_radius {
    border-radius: 2px;
}

/*修改基础字号*/

.WB_frame_a, .WB_frame_a_fix, .WB_frame_b, .WB_frame_c, .WB_frame_d, .WB_main_l, .WB_main_c, .WB_main_r, .WB_main_cr, .WB_main_a, .WB_toptips {
    font-size: 14px;
}

.WB_text {
    line-height: 26px;
}

/*正文字号*/

.W_f14 {
    font-size: 15px;
}

/*基础字号*/

.W_btn_a, .W_btn_b, .W_btn_c, .W_btn_d {
    font-size: 14px;
}

.WB_from.S_txt2 {
    font-size: 13px;
    display: none;
}

a.S_txt2 {
    color: #D0D0D0;
    font-size: 13px;
}

a.S_txt2:hover {
    color: #eb7350;
}

a.W_f14.W_fb.S_txt1 {
    font-size: 16px;
}

/*其他页面兼容性*/

/*私信页面*/

.private_dialogue_box .private_dialogue_body {
    height: 700px;
}

.UI_scrollView {
    height: 700px;
}

.webim_contacts_mod.S_bg2 {
    margin-top: 130px;
}
}
