@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("anicat.tv"), domain("kinomaza.com") {
html, body {background: url(/*[[USERIMAGE]]*/) no-repeat fixed !important; -webkit-background-size: cover !important; background-size: cover !important;}
  /*Head*/
  .wrapper {padding: 0 !important;}
  .ra_top {display: none !important;}
  /*Кнопки*/
  #menu ul.dualfish > li {padding: 0 !important;}
  #menu ul.menu > li > a, #menu ul.menu > li.active > a, #menu ul.menu > li.current > a {
  opacity: 1 !important;
  position: relative !important;
  color: white !important;
  font-weight: bold !important;
  text-decoration: none !important;
  text-shadow: -1px -1px #000 !important;
  padding: 10px !important;
  outline: none !important;
  background-color: #000 !important;
  background-image: linear-gradient(45deg, rgba(255,255,255,.0) 30%, rgba(255,255,255,.8), rgba(255,255,255,.0) 70%), radial-gradient(190% 100% at 50% 0%, rgba(255,255,255,.7) 0%, rgba(255,255,255,.5) 50%, rgba(0,0,0,0) 50%) !important;
  background-repeat: no-repeat !important;
  background-size: 200% 100%, auto !important;
  background-position: 200% 0, 0 0 !important;
  box-shadow: rgba(0,0,0,.3) 0 2px 5px !important;
} 
  #menu ul.menu > li.active > a:active, #menu ul.menu > li.current > a:active {top: 1px !important; box-shadow: none !important;}
  #menu ul.menu > li.active > a:hover, #menu ul.menu > li.current > a:hover {transition: .5s linear !important; background-position: -200% 0, 0 0 !important;}
  /*Правая панель*/
  .wide .modulecontent {background: none !important;}
  table.tab_genre a, table.tab_year a {color: black !important;}
  table.tab_genre a:hover, table.tab_year a:hover {background-color: red !important; color: white !important;}
  table.tab_genre td, .wide .modulecontentinner, table.tab_year td {border: none !important;}
  #vk_groups {display: none !important;}
  /**/
  .global {background: none !important;}
  #region9, #region11 {display: none !important;}
  /**/
  #search #mod_search_searchword, #search .button {background: none !important; border: 1px solid red !important;}
  #search .button, .button, input[type="submit"] {background: none !important; color: black !important;}
  #search .button:hover, .button, input[type="submit"] {background: none !important; background-color: red !important; color: white !important;}
  /**/
  .amk2, .mgbox {display: none !important;}
  object[name="prmbn"] {display: none !important;}
}
