css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(userstyles.org) {
html {margin:0!important;}
body {padding:5px!important;margin:0 auto 0 auto!important;width:1000px;color:#7594c1!important;background-color:#fff!important}
h2, h3, h1, p {color:#7594c1!important;}
h2, h3 {margin:5px 0 5px 0!important;}
ul {padding:0!important}
ul, li, p {font-size:12px!important;}
li {list-style:none!important;clear:both!important}

a, a:visited {color: #7594c1!important;margin:1px!important;text-decoration:none!important}
a:hover,  a:active {color:#426395 !important;text-decoration:underline!important;}

a.unranked, a.unranked:hover {padding:2px 2px 2px 20px;color:#426395!important;background-image:url(http://userstyles.org/images/star_0.png)!important;background-position:0 0!important;background-repeat:no-repeat!important;max-width:200px!important;}

a.poor-rank, a.poor-rank:hover {display:none!important;}

a.medium-rank, a.medium-rank:hover {padding:2px 2px 2px 20px;color:#426395!important;background-image:url(http://userstyles.org/images/star_05.png)!important;background-position:0 0!important;background-repeat:no-repeat!important;max-width:200px!important;}

a.good-rank, a.good-rank:hover {padding:2px 2px 2px 20px;color:#426395!important;background-image:url(http://userstyles.org/images/star_1.png)!important;background-position:0 0!important;background-repeat:no-repeat!important;max-width:200px!important;}

#logo {margin:6px 0 15px 20px !important;background:none!important;padding:0 8px 4px 8px !important;}
#logo a {color:#7594c1!important}

#search-submit {border:0!important;padding:1px!important}

#navigation {margin:21px 0 0 0!important;padding:0 30px 0 0!important;}
#navigation .current-page {border-top: 1px solid #E0E7F1!important;border-left: 1px solid #E0E7F1!important;border-right: 1px solid #E0E7F1!important;}
#navigation a:hover {border:1px solid #7594c1!important;border-bottom:0!important;color:#426395!important;background:#E0E7F1!important;}
#navigation a {color: #7594c1!important;background:#E0E7F1!important;}

#header-bar {background:none!important;border:1px solid #E0E7F1!important;margin:0 0 5px 0!important;-moz-border-radius:8px!important;}
#main-text {width:auto!important;margin:5px 0 0 0!important;padding:0 10px 0 10px!important;float:left!important;border:1px solid #E0E7F1!important;background:#fff!important;-moz-border-radius:8px!important;}
#sidebar {width:980px!important;margin:0!important;padding:0 10px 0 10px!important;float:left!important;border:1px solid #E0E7F1!important;background:#fff!important;-moz-border-radius:8px!important;}
#sidebar * :hover {list-style:disc!important;text-decoration:none!important}
#sidebar h2 {-moz-border-radius:5px!important;margin:10px 0 10px 0!important;padding:6px!important;background:#E0E7F1!important;text-align:left!important;width:auto!important;}
#sidebar ul {-moz-column-count:3!important;}
#sidebar ul li {margin:0 0 0 15px!important;padding:3px 0 3px 0!important;text-align:left!important;text-indent:-4px;border-bottom:1px dashed #E0E7F1!important}
#sidebar ul li a {white-space:nowrap;margin:0!important;padding:0!important;}
#sidebar ul li:hover {border-color:#7594c1!important}
.all-link, .post-your-style {width:auto!important;text-align:center!important;padding:10px 0 0 0}

#miscellaneous {color:#fff !important;text-align:center!important;margin:10px 0 10px 0!important}
#miscellaneous a, #logo a:hover, #navigation a:hover {text-decoration:none!important;}
#account-link a {color:#426395!important;text-decoration:none!important;}
#stylish-install {color:#426395!important;}
#style-list {-moz-column-count: 3 !important;-moz-border-radius:5px!important;border:1px solid #E0E7F1!important;padding:6px!important;clear:both!important}
#style-list li {margin:7px 0 7px 0!important;}
#style-show {margin-top:15px!important;float:left!important;text-align:left!important;width:100%!important;-moz-border-radius:11px!important;border:1px double #E0E7F1!important;padding:8px!important;}
#style-meta {float:right!important;text-align:left!important;background:#fff!important;-moz-border-radius:11px!important;color:#426395!important;border:3px double #E0E7F1!important;padding:8px!important;margin-top:15px!important;}
#style-show > h3 {text-align:center!important}
#show-button {margin:0 0 0 456px!important}
#style-show > #long-description {min-height:100px!important}
#style-show > h2, #style-show > ul {text-align:center!important}
#style-show > ul > li {line-height:1.7em!important}

.screenshot-table {clear:both;margin:0 auto 0 auto!important;}
.category-meta, h1, #category-search {text-align:center!important;}
.category-style-top {width:100%!important;float:right!important}
.category-style-top ul li {line-height:1.7em!important;clear:both!important}
.category-style-top ul li:hover, .category-meta ul li:hover {list-style:disc!important;color:#426395!important;}
.category-style-top ul, 
.category-meta ul {-moz-column-count:3!important;-moz-border-radius:5px!important;margin:0!important;padding:2px!important;background:none!important;text-align:center!important;background:#E0E7F1!important}

#reviews {margin:0!important;padding:0 10px 0 10px!important}
.review-container {margin:0!important;padding:0!important;}
.review-container h3 {padding:5px!important;background:#E0E7F1!important;-moz-border-radius:5px!important;}
.review-container blockquote {padding:0 0 0 8px!important;margin:0 0 30px 0!important}
.screenshot-box, #screenshots {text-align:center!important;}
.form-controls {-moz-border-radius:11px!important;border:4px double #E0E7F1!important;margin:4px 0 4px 0!important;padding:10px!important}
.form-controls th {padding:5px!important;font-size:12px}

li a.edit-link {text-transform: uppercase;float:right!important;margin:auto!important;margin:0 265px 20px 0!important;clear:both!important;font-size:9px!important;font-weight:bold!important;}

span.stars {float:right!important;}
span.list-average-rating {float:left!important;}

.simple-link-ads, #simple-link-ads {display:none!important;}

img[src*="/images/feed-icon-16x16.png"] {margin:13px!important;float:right}
img[class*="screenshot single-screenshot"] {padding:5px!important;border: 3px double #E0E7F1!important;-moz-border-radius:7px!important}

#view-code {background:#E0E7F1!important;color:#000!important;border: 1px dashed #7594c1!important;margin:5px!important;overflow:auto;max-height:500px!important}
#stylish-code {font-size:10px!important;font-family:Verdana!important;}

}
