css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("elastic.co") {

#elastic h1 a {
	width: 177px !important;
	background: url(http://forumbgz.ru/user/upload/file34446.png) no-repeat 0 0 !important;
	background-size: 177px 55px !important;
}

#pageheader {
	background: #74b73f !important;
}

body.ui-site #main .page-title {
	background: #74b73f !important;
}

a {
	color: #74b73f !important;
}

#guide a code {
    color: #74b73f !important;
}

.button {
	background: #74b73f url(https://www.elastic.co/static/img/go.png) no-repeat right -1px !important;
}
  
.button:hover {
	background: #509E12 url(https://www.elastic.co/static/img/go.png) no-repeat right -1px !important;
}

.button.big {
	background: #74b73f url(https://www.elastic.co/static/img/go-lg.png) no-repeat right -2px !important;
}
      
.button.big:hover {
	background: #509E12 url(https://www.elastic.co/static/img/go-lg.png) no-repeat right -2px !important;
}
      
.button.icon {
	background-image: none !important;
}
    
.button a, a.button {
	color: white !important;
}
    
.simple-pagination li.active .current {
	color: #2e2e2e !important;
}
    
#pageheader h2 {
	color: #2e2e2e !important;
}
    
h1, h2, h4, h5, h6 {
	color: #2e2e2e !important;
}
    
.dropdown li {
	background: #2e2e2e !important;
}
      
.dropdown li a, .dropdown li >span {
	color: white !important;
}

.dropdown li:hover > a, .dropdown li:hover >span {
	background: #4D4D4D !important;
}

#guide pre.prettyprint {
	border-left: 3px solid #74b73f !important;
}
}
