Floatingx{
	from{
		-webkit-transform:translate(0,0px)
	}

	65%{
		-webkit-transform:translate(0,15px)
	}

	to{
		-webkit-transform:translate(0,0px)
	}

}

@-moz-keyframes
Floating{
	from{
		-moz-transform:translate(0,0px)
	}

	65%{
		-moz-transform:translate(0,15px)
	}

	to{
		-moz-transform:translate(0,0px)
	}

}

::selection{
	background-color:#018dcb;
	color:white;
	-moz-opacity:0.75;
	-khtml-opacity:0.75;
	-webkit-opacity:0.75;
	opacity:0.75;
	filter:alpha(opacity=75)
}
}
