@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ukraina.ru") {
  .tvbox_slideshow,
  .socialbox,
  .header_socialbox,
   .banner-footer,
    .social-likes-author {
  display: none !important;
}
  

.top_menu .top_menu_item {
    padding: 5px 5px 5px 0 !important;
}
  .top_menu {
  width: 100% !important;
}
  
.top_menu .top_menu_title {
    padding: 5px 0 !important;
    width: 160px !important;
}

section.article_header {
    bottom: 0 !important;
    height: 250px !important;
    left: 0 !important;
    margin: 0 auto !important;
    position: relative !important;
    width: 1000px !important;

}
    
    .top_menu .top_menu_list {
    width: 75% !important;
          max-height: 40px !important;
    min-height: 2px !important;
        }

.top_menu .top_menu_control {
    float: right;
    padding: 2px 0;
    width: 120px;
}
    
    .header_section {
    background: none repeat scroll 0 0 #000;
    height: 110px !important;
    padding-left: 20px !important;
    padding-right: 20px !important;
    padding-top: 40px !important;
}
    
 
.top_panel {
    left: 0 !important;
    min-height: 30px !important;
    min-width: 800px !important;  
} 
    nav.tvbox.tvbox_rubric.tvbox_rubric_spec {
    height: 340px !important;
}
  
    nav.tvbox {
    display: block;
    min-height: 20px !important;
    padding-top: 2% !important;
}
    section.article_header .description {
    bottom: 0 !important;
    position: relative !important;
    width: 100% !important;
}
    .full_text p {
    margin-bottom: 4px !important;
    margin-top: 10px !important;
}
    .marker-quote1:before {
    border-image: none;
    border-style: solid;
    border-width: 0;
    }
    
    .marker-quote1 {
    font: italic 800 16px/1.4 Arial,Helvetica,sans-serif !important;
    margin: 0px 0px 0px 0px !important;
    padding: 4px 4px 4px 4px !important;
    position: relative !important;
}
 .marker-quote2 {
    font: italic 800 16px/1.4 Arial,Helvetica,sans-serif !important;
    margin: 0px 0px 0px 0px !important;
    padding: 4px 4px 4px 4px !important;
    position: relative !important;   
     border-right: 0 solid #e0e0e0 !important;
     border-left: 0 solid #e0e0e0 !important;
 }   
    
    .marker-quote2:before {
    border-left: 0px solid #e0e0e0 !important;
    border-width: 0;
    bottom: 0;
    content: "";
    display: block;
    left: 0;
    position: absolute !important;
    top: 0;
    width: 0 !important;
}
    div.article_inject_in div.inject_photo_illustration.inject img {
        width: 300px !important;
        height: 170px !important;
        margin: 0;
        padding: 0 0 0 150px;
     
    }

.article_inject_photo {
    background: none repeat scroll 0 0 #ffffff !important;
    border-bottom: 0px solid #e0e0e0 !important;
    margin: 10px !important;
    padding: 4px 0px !important;
}
    
    .article_inject_photo .media_copyright {
            background: none repeat scroll 0 0 #ffffff !important;
    border-bottom: 0px solid #e0e0e0 !important;
    font-size: 6px !important;
    line-height: 1 !important;
    margin: 2px 0 0 !important;
    text-align: center !important;
}
    .article_person {
    color: #fff !important;
    margin: 0 !important;
    padding: 10px 10px 0 !important;
}
    
    section.article_header .description > div h1 {
    border: 0 none !important;
    color: #fff;
    font-size: 26px;
    font-weight: bold;
    margin-bottom: 5px;
    margin-top: 20px;
    text-shadow: 4px 4px 4px rgba(0, 0, 0, 1);
}
    
    section.article_header {
    bottom: 0 !important;
    height: 150px !important;
    left: 0 !important;
    margin: 0 auto !important;
    position: relative !important;
    width: 1000px !important;
}
.article_lead {
    font-size: 14px !important;
    line-height: 1.4 !important;
}   

}
