/* Hello, and thank you for downloading my style! */
/* This is "FurAffinity - Project Fursona! +", a style inspired by the video game "Persona" (While still being something unique...), and intend to be a little customisable. */
/* It is an optimized and enhanced version of "Project Fursona!", the first custom CSS I did.*/
/* If you have requests, want to leave a kind shout, or anything else, you can find me here: http://www.furaffinity.net/user/cuteness-overlord/ */
/* I used huge parts of the works of Artex. ( http://www.furaffinity.net/user/artex./ ) and iErrored ( www.furaffinity.net/user/ierrored ) in this style. */
/* Hope you'll enjoy this style ! Have fun browsing you "new" FA ! */

@-moz-document domain("www.furaffinity.net"), domain("sfw.furaffinity.net") {
  /* Font Swap */
    table, tr, td, th, body {
        font-family: "Trebuchet", Helvetica, sans-serif;
        -webkit-font-smoothing: antialiased !important;
        text-shadow: 0px 0px 3px rgba(233,219,204,0.70), 1px 1px 2px #6F393A;
    }
    ul.dropdown, ul.dropdown li, ul.dropdown ul {
        font-family: "Trebuchet", Helvetica, sans-serif;
        -webkit-font-smoothing: antialiased !important;
    }
  /* Artist Informations Tweak */
    td.alt1.user-info > span {
        color: #6F393A;
        font-weight: bold !important;
    }
  /*-------------------------------------*/
    
  /* Recoloring stuff / Altering Borders and Backgrounds... */
    body, body>div, hovertable_body, .innertable, .logo, .menu li ul li { 
        background-color: #D8C0B6 !important;
    }
    body > div {
        background: url(http://puu.sh/grHHC/c7e12d2aaf.png) !important;
    }
    #gallery .cat > .maintable > tbody > tr:nth-child(1) > td,
    #scraps .cat > .maintable > tbody > tr:nth-child(1) > td,
    #favorites .cat > .maintable > tbody > tr:nth-child(1) > td {
        background-color: #0480A4 !important;
    }
    .innertable > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) {
        background-color: #0480A4 !important;
    }
    #gallery .cat > .maintable, #scraps .cat > .maintable, #favorites .cat > .maintable {
        border-collapse: collapse;
    }
    #news{
        margin:45px auto 0 !important;
        border-radius:0 !important;
        border: 1px solid #0480A4 !important;
        width:97.5% !important;
        background-color: #E9DBCC !important;
        color: #6F393A !important;
    }
  	#admin_notice_do_not_adblock TD{
		    background-color: #E9DBCC !important;
	    	position: relative;
        color: #6F393A !important;
  	}
    #admin_notice_do_not_adblock{
	    	z-index:50 !important;
	    	top: 10px !important;
    		position:relative !important;
  	}
  /* Main Tables */
    .innertable {
        background-color: #D8C0B6 !important;
    }
    .maintable {
        background-color:#0480A4 !important;
        color: #D8C0B6;
    }
    .cat {
        background-color:#0480A4 !important;
        border:none !important;
        color: white !important;
    }
    .alt1 {
        background-color: #E9DBCC !important;
        color: #0480A4 !important;
    }
    .alt2 {
        background-color:#E9DBCC !important;
        color: #0480A4;
    }
    .ldot {
        color: #6F393A !important;
        border: none !important;
        border-left: 1px dotted #0480A4 !important;
    }
    .bdot {
        border: none !important;
        border-bottom: 1px dotted #0480A4 !important;
    }
    center.flow s > a, center.flow img {
        border-radius: 0 !important;
    }
    center.flow.userpage-submissions b.active a, center.flow.userpage-favorites b.active a {
        border: none !important;
    }
  /* Profile Tabs */
    .tab a {
        background-color:#E9DBCC !important;
        border-left:3px double #0480A4 !important;
        border-right:3px double #0480A4 !important;
        border-top:3px double #0480A4 !important;
    }
    .tab u {
        background-color: #0480A4 !important;
        border: none !important;
        color: white !important;
    }
  /* Comment Boxes */
    .container-comment {
        background-color:#E9DBCC !important;
        border: none !important;
        border-bottom: 2px solid #0480A4 !important;
        color: #0480A4 !important;
    }
    .container-comment TH {
        background-color:#0480A4 !important;
    }
    .container-comment td.cat a {
        color:#ffffff !important;
        background-color: #0480A4 !important;
    }
  /* Shouts */
    .lead {
       background-color: #0480A4;
       height: 12px;
       border:none !important;
       color: white !important;
    }
  /*-------------------------------------*/
    
  /* Improve readability of text submissions */
    #submission table.maintable:nth-child(2) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) {
        color:black !important;
        background-color:white !important;
        padding:20px !important;
        width: 50% !important;
    }
    #submission table.maintable:nth-child(2){
        max-width:800px !important;
    }
    #submission table.maintable:nth-child(2) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) a {
        color:rgb(0,255,240) !important;
    }
  /*-------------------------------------*/
    
  /* Header and Footer Tweaks */
    .clearfix { /* I took this in Grey Rust's code... */
        padding-top: 38px !important;
    }
    #fa_header {
        width: 90% !important;
        height: 180px !important;
        left:1.5%;
        top: 14%;
        background: url("http://puu.sh/gvghw/0ba6f506e8.png") no-repeat scroll 0% !important;
        background-color: rgba(0,0,0,0.0) !important;
    }
    .footer {
        background: url(http://puu.sh/grHHC/c7e12d2aaf.png) !important;
        color: #93573F !important;
        font-size: 8px !important;
    }
  /*-------------------------------------*/
    
  /* Navigation Bar */
    .block-menu-top {
        z-index: 42;
        top:0px !important;
        position: fixed;
    }
    .block-menu-top a {
        color:#E9DBCC !important;
    }
    .block-menu-top a[href^="/user/"] {
        color:#FFFFFF !important;
    }
    .block-menu-top a[href^="/logout/"]:hover {
        color: #6F393A !important;
    }
    .block-menu-top td {
        color: #E9DBCC !important;
        background-color:#0480A4 !important;
        border-bottom: 2px solid #6F393A;
        opacity:0.9;
    }
    ul.dropdown > li { /* Navbar size reduction and font testing */
        line-height:0.8em !important;
    }
    ul.dropdown ul {
        background-color: #443533 ! important;
    }
    ul.dropdown ul a:hover {
        background-color: #046CA5 !important;
    }
    ul.dropdown li hr {
        border: 0;
        height: 2px;
        background-image: linear-gradient(to left, rgba(233,219,204,0), rgba(233,219,204,1.00), rgba(233,219,204,0));
    }
    li#sfw-toggle.active {
        background-color: #A84E4E !important;
    }
  /*-------------------------------------*/
  
  /* Fancy Stuff */
  /* Submission Border */
    #submissionImg {
       border: 3px #D8C0B6 !important;
       background:#D8C0B6 !important;
    }
  /* Fade-In the description popup... */
    #description_popup {
       animation:fadein 0.5s;
       -webkit-animation:fadein 0.5s;
    }
    @keyframes fadein {
       from {opacity:0;}
       to {opacity:1;}
    }
    @-webkit-keyframes fadein {
       from {opacity:0;}
       to {opacity:1;}
    }
    #description_popup:first-of-type{
       opacity:0;
    }   
  /*Hover Transitions*/
    a[href^="/watch/"] font:hover {
       color:gold !important;
    }
    .tab a {
       height:20px !important;
       transition: padding 0.2s;
    }
    .tab a:hover {
       padding-top:8px !important;
       color:#0480A4 !important;
    }
    .r-general, .r-mature, .r-adult {
       overflow:visible !important;
    }
    .r-general a:hover, .r-mature a:hover, .r-adult a:hover {
       transform:scale(1.05,1.05);
       -webkit-transform:scale(1.05,1.05);
    }
  /* Usernames Dropdown Menu */
    .menu li ul li:hover {
       background-color: #97574E !important;
    }
    .menu li ul li {
       background-color: #0480A4 !important;
       border: 1px solid #D8C0B6 !important;
    }
  /* Checked Submissions */
    center.flow.messagecenter b.checked {
       background-color: #0480A4 !important;
       border-radius: 0 !important;
    }
  /* Better Submissions/Favorites */
    center.flow.userpage-submissions.twolines, center.flow.userpage-favorites.twolines {
       background-color: #D8C0B6 !important;
       border-top: 5px double #1398A9 !important;
       border-bottom: 5px double #1398A9 !important;
       border-left: 1px solid #1398A9 !important;
       border-right: 1px solid #1398A9 !important;
    }
  /*-------------------------------------*/
  
  /* Links Colors */
  .footer A:link {
       color: #6F393A !important;
    }
  .footer A:visited {
       color: #93573F !important;
    }
  .footer A:hover {
       color: #26A4AD !important;
    }
  .alt1 A:link {
       color: #6F393A !important;
    }
  .alt1 A:visited {
       color: #93573F !important;
    }
  .alt1 A:hover {
       color: #26A4AD !important;
    }
  .alt2 A:link {
       color: #6F393A !important;
    }
  .alt2 A:hover {
       color: #26A4AD !important;
    }
  .lead A:link {
       color: white !important;
    }
  .lead A:link {
       color: #E9DBCC !important;
    }
  .lead A:hover {
       color: white !important;
    }
  .addpad A:link {
       color: white !important;
    }
  .addpad A:visited {
       color: white !important;
    }
  .addpad A:hover {
       color: #E9DBCC !important;
    }
  .cat A:link {
       color: white !important;
    }
  .cat A:visited {
       color: white !important;
    }
  .cat A:hover {
       color: #E9DBCC !important;
    }
  /*-------------------------------------*/
    
  /* --------------------- Custom BB-Code Stuff --------------------- */
  /* Custom Quotes */
    .bbcode_quote {
        background-color: #D8C0B6 !important;
        border: 5px double #1398A9 !important;
        color: #6F393A !important;
    }
    .bbcode_quote A:link {
        color: white !important;
    }
    .bbcode_quote A:visited {
        color: white !important;
    }
    .bbcode_quote A:hover {
        color: #0480A4 !important;
    }
  /* Custom Horizontal Lines (HR) */
    .bbcode_hr {
        border: 0;
        height: 2px;
        background-image: linear-gradient(to left, rgba(19,152,169,0), rgba(19,152,169,0.75), rgba(19,152,169,0));
    }
  /*-------------------------------------*/
    
  /* 'Last Minute' Fixes */
  /* Messages / Submissions */
  div#messagecenter-submissions, div#messagecenter-other {
      background-color: #E9DBCC !important;
      border: 1px solid #0480A4 !important;
  }
  div#messagecenter-other div.no-messages {
      background-color: #0480A4 !important;
      color: white !important;
  }
  div#messagecenter-submissions h1, div#messagecenter-submissions h3.date-divider, div#messagecenter-other fieldset h3 {
      background-color: #0480A4 !important;
      border: none !important;
      border-top: 5px double #1398A9 !important;
      border-bottom: 5px double #1398A9 !important;
      border-left: 1px solid #1398A9 !important;
      border-right: 1px solid #1398A9 !important;
      color: white !important;
  }
  div#messagecenter-other ul.message-stream {
      background-color: #D8C0B6 !important;
      border: 1px solid #0480A4 !important;
  }
  div#messagecenter-submissions form {
      color: #0480A4 !important;
  }
  div#messagecenter-submissions form A:link, div#messagecenter-other ul.message-stream A:link {
      color: #6F393A !important;
  }
  div#messagecenter-submissions form A:visited, div#messagecenter-other ul.message-stream A:visited {
      color: #93573F !important;
  }
  div#messagecenter-submissions form A:hover, div#messagecenter-other ul.message-stream A:hover {
      color: #26A4AD !important;
  }
  div#messagecenter-other ul.message-stream li {
      color: #0480A4 !important;
  }
  div#messagecenter-other ul.message-stream li.hover {
      background-color: #E9DBCC !important;
  }
  div#messagecenter-other ul.message-stream li.checked {
      background-color: #E9DBCC !important;
  }
  div#messagecenter-other div.global-controls {
      background-color: #0480A4 !important;
  }
  /* Staff List */
  .staff-table {
      background-color: #E9DBCC !important;
      border: 1px solid #0480A4 !important;
      color: #0480A4 !important;
  }
  .staff-table A:link {
       color: #6F393A !important;
    }
  .staff-table A:visited {
       color: #93573F !important;
    }
  .staff-table A:hover {
       color: #26A4AD !important;
    }
  td > h1 {
      color: #6F393A !important;
  }
  /* Moving the ads downward... */
  block-banners.clearfix {
      height: 230px !important;
  }
  .block-banners div.ads {
      left: auto !important;
      position: absolute !important;
      right: 10px !important;
      z-index: 15 !important;
		  top:50px;
  }
  /*-------------------------------------*/
    
  /* Credits */
  .footer:before {
        content: '"FurAffinity - Project Fursona! +" is a custom CSS made by Cuteness-Overlord. ( http://www.furaffinity.net/user/cuteness-overlord ) based on parts of the works of Artex. ( http://www.furaffinity.net/user/artex. ) and iErrored ( http://www.furaffinity.net/user/ierrored ).';
        color: #93573F;
        font-weight: bold;
        font-size: 8px;
    }
}
