css:
@-moz-document domain("miniclip.com")
{
body{background-color:#FFFFFF!important}
div[class="notification-bar error"]{display:none!important}
div[id="container"]{background-color:#FFFFFF!important}
div[class*="sky-wrapper"]{display:none!important}
}

@-moz-document url-prefix(http://www.miniclip.com/games/8-ball-pool-multiplayer/en/focus)
{
body{overflow: hidden;}
html[class^="wf-opensans-n"]{background:-moz-linear-gradient(left,#323540,#393B46,#464754,#393B46,#323540)!important}
object[id="game-embed"]{background:-moz-linear-gradient(left,#323540,#454653,#464754,#454653,#454653,#323540)!important}

body,div[id="container"]{height:520px!important;background:linear-gradient(#363843,#454653,#464754,#464754,#464754,#333540)!important}

*[class^="wf-loading"]{background:linear-gradient(#363843,#454653,#464754,#464754,#464754,#333540)!important}

section[id="game-container"]{margin-left:-120px!important}

header[class="expert-mode"],div[class^="game-toolbar"],div[class="tools"],#advert-holder{display:none!important}
div[class="focus-game layout-aga"]{margin-left:40px!important}
a[rel="nofollow"]{display:none!important}
div[id="pool-menu-before"]{display:none!important}
nav[id="pool-menu-after"]{display:none!important}
div[class="expert-game layout-aga"]{height:520px!important;padding-left:600px!important;margin-top:40px!important;margin-left:-230px!important;background:linear-gradient(#363843,#363843,#3D3F4B,#444552,#454653,#464754,#454653,#444552,#3D3F4B,#393B46,#333540)!important}
}
