css:
@-moz-document domain("vk.com") {
#pb_text {
    direction: rtl;
    font-family: tahoma;
    font-size: 13px;
	text-align: justify;
}
	.wide_column{
		width:70%;
	}
	.post_table {

border: 1px solid #74A4D3;
padding:15px;
}
.wall_module .post_info {
    direction: rtl !important;
    margin-left: 0 !important;
    /*margin-right: 10px !important;*/
    text-align: right !important;
	width:90% !important;
}
    .wall_module div.post_image {
    padding: 5px 0 !important;
    right: 0 !important;
			margin-right: 5px;
}
.post_full_like {
    left: 0 !important;
    right: auto !important;
}
.wide_wall_module .post_full_like_wrap, .post_fixed .post_full_like_wrap {
    float: left !important;
}
.post_like, .post_share {
    float: left !important;
}

	.post{
		padding: 0 0 16px !important;
	}
	
	.reply_field{
		width:95% !important;
	}
