css:
@-moz-document domain("vk.com") {
.wall_module .post_info {
    direction: rtl !important;
    margin-left: 0 !important;
    margin-right: 60px !important;
    padding: 20px 0 !important;
    text-align: right !important;
}
.wall_module div.post_image {
    padding: 20px 0 !important;
    right: 0 !important;
}
.post_full_like {
    left: 0 !important;
    right: auto !important;
}
.wide_wall_module .post_full_like_wrap, .post_fixed .post_full_like_wrap {
    float: left !important;
}
.post_like, .post_share {
    float: left !important;
}
.is_rtl1 .info_footer.sm {
    direction: rtl !important;
    text-align: right !important;
}
.info_footer.sm {
    direction: ltr !important;
    text-align: left !important;
}
.wide_wall_module .reply_info {
    margin-left: 0 !important;
    margin-right: 20px !important;
width: 517px !important;
}
.wall_module .post_actions, .wall_module .reply_actions {
    direction: ltr !important;
    float: left;
    left: 0 !important;
    right: auto !important;
}
.wall_module .post_delete_button, .wall_module .post_edit_button, .wall_module .reply_delete_button, .wall_module .reply_edit_button {
    float: left !important;
}
.wall_module .post_actions_wrap, .wall_module .reply_actions_wrap {
    float: left !important;
}
.page_fronly {
    margin: 2px 4px 0 0 !important;
}
.info_footer.sm {
    clear: both !important;
 
}
.reply_text > div {
    float: right !important;
}
.reply_text .author {
    float: right !important;
}
 
.post_info .replies {
    color: #888 !important;
    font-size: 11px !important;
}
.wall_post_text {
    font-size: 12px !important;
}
 
.reply_image {
    height: 16px !important;
    width: 16px !important;
    border-radius: 100% !important;

}
.published_by_wrap {
    direction: ltr !important;
    text-align: left !important;
}
 
.wall_module .reply_info {
    margin-left: 0 !important;
    margin-right: 19px !important;
    width: auto  !important;
}
.post_full_like_wrap.sm {
    float: left !important;
}
 
.reply:nth-child(2n) {
    background: none repeat scroll 0 0 #f9f9f9 !important;
}
.reply {
    padding-left: 5px !important;
    padding-right: 5px !important;
}
.post_image img {
    border-radius: 100% !important;
}


#page_body {
    width: 731px !important;
}
#page_layout {
    width: 891px !important;
}
.wide_wall_module .post_info {
    width: 637px !important;
}
.wide_wall_module .wall_text, .wide_wall_module .wall_post_text {
    width: 637px !important;
}
.wide_wall_module div.published_by_date {
    width: 580px !important;
}
.page_wide_no_narrow .wall_reply_text {
    width: 617px !important;
}

.wide_column {
    width: 497px !important;
}
.page_wide_no_narrow .wide_column {
    width: auto !important;
}

.wall_text {
    width: 437px !important;
}
.wall_post_text {
    width: 437px !important;
}

#profile .wall_reply_text {
    width: 398px;
}
.wall_module .post_info {
    width: auto !important;
}
.wide_wall_module .wall_reply_text {
    width: 577px !important;
}
#main_feed .wall_reply_text {
    width: 628px !important;
}
#main_feed .post {
    border-bottom: none !important;
}
.feed_row {
    border-radius: 2px !important;
    box-shadow: 0 0 2px 1px #8dabca !important;
    margin: 9px -10px !important;
    padding: 0 10px !important;
}
}
