@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("starhit.ru") {
html, body {background: url(http://ya-vidoc3000.narod.ru/anime/25.jpg) no-repeat 50% 70% fixed !important; -webkit-background-size: cover !important; background-size: cover !important;}
  /*Реклама*/
  .footer, .footer-projects, .promo-footer, .header-top {display: none !important; visibility: hidden !important;}
  a[href*="adriver.ru"], div[id*="banner"], #ad_ph_1, #ad_ph_2, .yandex-direct, .teasers-block-container {display: none !important;}
  #popup-once, .popup-fade {display: none !important;}
  a[href*="otpusk"], .spec-menu ul .malahov-vacation-link {display: none !important;}
  /**/
  .header {background-color: transparent !important;}
  .header-menu, .header-menu ul, .spec-menu, .spec-menu ul .malahov-vacation-link {background: none !important; text-transform: none !important;}
  .header-menu .spec-link span,.header-menu .spec-link div, .header-menu .spec-link {background: none !important; color: #777674 !important;}
  .header-menu .spec-link span:hover, .spec-menu-inner {background: none !important;}
  a[href*="/hudeem/"] {display: none !important;}
  /*Кнопки*/
 .header-menu a, .spec-menu ul a {
  display: inline-block !important;
  color: black !important;
  font-weight: bold !important;
  text-decoration: none !important;
  text-shadow: rgba(255,255,255,.5) 1px 1px, rgba(100,100,100,.3) 3px 7px 3px !important;
  padding: 5px !important;
  outline: none !important;
  border-radius: 3px / 100% !important;
  background-image:
   linear-gradient(45deg, rgba(255,255,255,.0) 30%, rgba(255,255,255,.8), rgba(255,255,255,.0) 70%),
   linear-gradient(to right, rgba(255,255,255,1), rgba(255,255,255,0) 20%, rgba(255,255,255,0) 90%, rgba(255,255,255,.3)),
   linear-gradient(to right, rgba(125,125,125,1), rgba(255,255,255,.9) 45%, rgba(125,125,125,.5)),
   linear-gradient(to right, rgba(125,125,125,1), rgba(255,255,255,.9) 45%, rgba(125,125,125,.5)),
   linear-gradient(to right, rgba(223,190,170,1), rgba(255,255,255,.9) 45%, rgba(223,190,170,.5)),
   linear-gradient(to right, rgba(223,190,170,1), rgba(255,255,255,.9) 45%, rgba(223,190,170,.5));
  background-repeat: no-repeat !important;
  background-size: 200% 100%, auto, 100% 2px, 100% 2px, 100% 1px, 100% 1px !important;
  background-position: 200% 0, 0 0, 0 0, 0 100%, 0 4px, 0 calc(100% - 4px) !important;
  box-shadow: rgba(0,0,0,.5) 3px 10px 10px -10px !important;
}
.header-menu a:hover, .spec-menu ul a:hover {
  transition: .5s linear !important;
  background-position: -200% 0, 0 0, 0 0, 0 100%, 0 4px, 0 calc(100% - 4px) !important;
}
.header-menu a:active, .spec-menu ul a:hover {
  top: 1px !important;
}
  /*Content*/
  .wrap {background-color: pink !important;}
  .content {background: none !important;}
  .latest-news {
-webkit-box-shadow: 0px 0px 30px 0px rgba(255, 0, 255, 1);
box-shadow: 0px 0px 30px 0px rgba(255, 0, 255, 1);
    margin: 0 !important;
    border: 0 !important;
  }
  .latest-news .fade-top, .latest-news .fade-bottom {background: none !important;}
  .latest-news-container {background-color: white !important; opacity: 0.7 !important;}
  /**/
}
