#page-header .inner {
  height : 60px;
}

#page-header .share-links {
  visibility : hidden;
}

#page-content {
  color : #fff;
}

#page-header .nav {
  background-color: #191e28;
}

#page-header .nav-link:hover, #page-header .nav-link.active {
color: #fff;
}

#page-header.broadcast {
height: 60px;
}

.btn-hollow {
  border: solid 1px #191e28;
}
  
.chat .msgs {
  background : #191e28;
	/* background: rgba(0,0,0,.25); */
}

.chat-activity {
  border-bottom: 1px solid #191e28 !important;
}
  
.chat-activity .message{
  color : #B8B8B8;
}
  
.chat-activity .username {
