@-moz-document domain("youtube.com")
{
    /*
______________________________________________
     __   __     __  ___         ___ 
    |__) |__) | /__`  |  | |\ | |__  
    |    |  \ | .__/  |  | | \| |___   v. 2.0                           
______________________________________________
By: theBADH

A clean, fresh new look for your YouTube space.
*/
    
/* Adjust Shadows and Margins to Align Items in Grid: */
.yt-lockup.yt-lockup-grid, .yt-lockup-content
{
    padding: 5px 5px 5px 5px;
}
.yt-lockup.yt-lockup-grid, .yt-lockup-thumbnail, .yt-card
{
    box-shadow: 0 2px 4px rgba(0, 0, 0, .5);
}
.yt-lockup.yt-lockup-grid
{
    margin-bottom: 5%;
    background: #FFFFFF;
    box-shadow: 0 2px 4px rgba(0, 0, 0, .5);
}
    
/* Adjust Shelf Header Margins: */
.multirow-shelf .yt-uix-expander-head
{
    margin-top: 10px;
}
    
/* Align Video Thumbnails: */
.browse-list-item-container .branded-page-module-title .video-thumb
{
    margin-right: 0px;
}
    
/* Handle Changes Between Stage Modes: */
.watch-stage-mode #theater-background
{
    background: none !important;
}
    
/* Remove the Border When the Lights Go Off: */
.ytcenter-lights-off #player-api
{
    border: none;
}
    
/* Remove White Backgrounds From Certain Text: */
.yt-ui-ellipsis
{
    background: none;
}
    
/* Set Background (I did NOT create this image) */
#body-container
{
    position: relative;
    overflow: hidden;
}
#body-container:before
{
    content: "";
    position: absolute;
    width: 200%;
    height: 200%;
    top: 0%;
    left: 0%;
    z-index: -1;
    background: url(/*[[background]]*/) fixed;
}

/*______________________________________________*/
    
/* Basic Font Formatting: */
h2, body
{
    font-family: "Segoe UI Light";
    text-transform: uppercase;
}
    
/* Modify Button Properties: */
.compact-shelf .yt-uix-shelfslider-next, .compact-shelf .yt-uix-button-shelf-slider-pager
{
    box-shadow: 0 2px 4px rgba(0, 0, 0, .5);
    background: white;
}
    
/*______________________________________________*/
    
/* Desk View Style: */    
/* Adjust Margins and Set "Flashcard" Backgrounds for Search Results: (Flashcard Image Borrowed from Clipartbest.com) */
#results .yt-lockup-tile
{
    margin-left: 30px;
}
#results .yt-lockup-content
{
    box-shadow: 0 2px 4px rgba(0, 0, 0, .5);
    padding-left: 20px;
    font-size: 1.1em;
    background: url("http://www.clipartbest.com/cliparts/Kin/M98/KinM98rrT.png") no-repeat 15% 20%;
    background-size: 150% 150%;
}

/* Blue Post-its: */
#action-panel-details
{
    background: #D9FBCD;
    box-shadow: 0 2px 4px rgba(0, 0, 0, .5);
}
    
/* Canary Yellow Post-its: */
.yt-card, #watch-header, #feed
{
    background: #FFFFA5;
}
    
/* Change Discussion Board and Sidebar to Fun Alternate Post-It Colors: */
#watch7-sidebar-contents
{
    background: #ADC1D6;
}
#watch-playlist
{
    left: 12%;
}
#watch-discussion
{
    background: #FFFFFF;
    box-shadow: 0 2px 4px rgba(0, 0, 0, .5);
}
.flex-width-enabled .content-alignment
{
    width: 50%;
    min-width: 800px;
    max-width: 1415px;
}
    
/* Modify Shelf Item Properties: */
.expanded-shelf-content-list
{
    padding: 5px 5px 5px 0px;
    box-shadow: 0 2px 4px rgba(0, 0, 0, .5);
    bottom: 5%;
    background: #FFFFFF;
}

