/* Slate-Grey Color Scheme: */
#appbar-guide-menu { box-shadow: 0.1em 1em 1em !important; }
#appbar-nav .yt-uix-button.yt-uix-button-epic-nav-item { color: #888; }
#appbar-guide-menu, #yt-masthead-container { background: #555; border-bottom: #111 !important; border-right: #111 !important; box-shadow: 0.1em 1em 1em !important; }
#masthead-appbar { background: #333; border: #111; }
#yt-masthead-container { box-shadow: 0.1em 0.1em 1em !important; }

a.yt-uix-button-epic-nav-item.selected, a.yt-uix-button-epic-nav-item.yt-uix-button-toggled, button.yt-uix-button-epic-nav-item.selected, button.yt-uix-button-epic-nav-item.yt-uix-button-toggled, .epic-nav-item.selected, .epic-nav-item.yt-uix-button-toggled, .epic-nav-item-heading { color: #ccc; }
.guide-item .guide-count, .guide-item-subtitle { color: #999; }
