#c4-header-bg-container, #browse-items-primary .item-section>li>.yt-lockup-tile, .branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box, .video-player-view-component branded-page-box { border-bottom: none !important; }
.branded-page-module-title, .branded-page-module-title a:visited, .branded-page-module-title a, .channel-header .branded-page-header-title .branded-page-header-title-link { color: #ccc; }
#circled-user-profiles-right, a.yt-uix-button.yt-uix-button-epic-nav-item, button.yt-uix-button-epic-nav-item, .epic-nav-item { color: #aaa; }
.lohp-newspaper-shelf .branded-page-module-title { padding-top: 2em !important; }

.primary-header-upper-section-wrapper { background: #111; }
#page.feed .yt-lockup-tile, #pl-header,  .compact-shelf .yt-shelf-grid-item, .lohp-medium-shelf, .lohp-video-thumb, .search-header, .shelf-description, .branded-page-related-channels-list .yt-lockup-content, #page:not(.feed) .expanded-shelf .yt-lockup-content, .lohp-large-shelf-container, .multirow-shelf-expander, .channels-content-item, .video-detail, .pl-video.yt-uix-tile, .yt-uix-shelfslider-item { box-shadow: 0 2px 4px rgba(0,0,0,.5); background: #FFFFA5; color: #111; padding: 0.5em; }
.lohp-medium-shelves-container { height: auto !important; }
#channel-subheader, #page.home .shelf-item .shelf-title-table, .feed-header { background: #333; border-bottom: none; margin: -1em; padding: 1em 1em 0.5em 1em; }
#page:not(.home) #channel-subheader { background: #555; }
#page:not(.home) .branded-page-module-title:not(.shelf-title-cell), #page:not(.home) .shelf-title-table { background: #333; margin: -1em; padding: 1em 1em 1.5em 1em; text-align: center; }
