@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("narutoplanet.ru") {
html, body {background: url(http://ya-vidoc3000.narod.ru/anime/26.jpg) no-repeat fixed !important; -webkit-background-size: cover !important; background-size: cover !important;}
  p {font-family: "Palatino Linotype", Cursive !important;}
  /*Header*/
  div.art-header-jpeg {background: none !important; height: 1px !important; width: 100% !important;}
  /*Content*/
  .art-post {border: 1px solid red !important; box-shadow: none !important;}
  .art-content-layout {width: 100% !important;}
  /*Панели*/
  .art-block {background: none !important;}
  .art-content-layout .art-sidebar1, .art-content-layout .art-sidebar2 {background: none !important;}
  /*Извращения xD*/
  embed[src*="/swf/"], iframe[src*="am15"], .amep, #amsb {display: none !important;}
  div[style*="text-align"] br {display: none !important;}
  .art-blockcontent a:link, .art-blockcontent li a:link, .art-blockcontent a {font-family: 'Sylfaen', cursive;}
  .item80, .item81 {display: none !important;}
  .art-article div+div[style*="text-align"] {text-align: left !important;}
  div[class*="banner"], .social-likes__widget {display: none !important;}
  /*Кнопки*/
  .art-blockcontent a {
  display: inline-block !important;
  color: black !important;
  font-weight: 100 !important;
  text-decoration: none !important;
  padding: 3px !important;
  outline: none !important;
  transition: 0.2s !important;
} 
.art-blockcontent a:hover {background: rgba(0,0,0,.9); color: white !important;}
.art-blockcontent a:active {background: black !important; color: white !important;}
  /**/
  .art-sheet-cc {background: none !important;}
  .art-post {background-color: white !important;}
  .art-block {border: 1px solid red !important;}
}
@-moz-document domain("narutoplanetru.chatovod.ru") {
  html, body {background: none transparent !important;}
.chatMain, .chatFooter, .chatEventDiv, .chatPopupMenuDiv1, .chatPopupMenuDiv2, .smilesBox, .chatPeople ul.chatPeopleList li.hover {background: none transparent !important;}
}
