@-moz-document url-prefix("http://us-moe-app.amz-aws.jp"), domain("amz-aws.jp") {
html { zoom: 2 !important; } input { font-family: sans-serif; } body { line-height: normal !important; }
img[src$='button/give2.png'] { width: 0px !important; height: 0px !important; padding: 55px 72px 0px 0px; background-image: url('http://us-moe-app.amz-aws.jp/img/hp_sp/friendRoom_img.png'); background-position: 0px -448px; background-size: 72px 795px; }
div > img[src*='event/event']:not([src*='_all']), img[src*='casino_npc'], img[src*='/half.php'] { width: 320px !important; height: 200px !important } img[src*='fitsample.php'] { max-width: 312px !important; }

.commonRoom { background-size: 320px 478px !important; } .roomMain { background-size: 108px 1410px; } .roomSub { background-size: 220px 999px !important; } .roomSub.new_keiziban { position: absolute; }
.new_hukidasi_center { margin: -1px 0px; padding: 1.5px 10px; box-sizing: border-box; } .new_selectL, .new_selectR { background-size: 192px auto !important; } .myroom.mn_button { background-size: 160px 380.5px !important; } .friendRoom { background-size: 72px 795px !important; }
.StatusMsgWindow > div[style^='background-color:#efffff'], .StatusMsgWindow > div[style='text-align:right'], #NowLoadingWindow, #LoadImg { display: none; } #commu_form > select { max-width: 320px; } #sleep_window { position: unset !important; } #menu_layout { z-index: 12 !important; } .dream_obi { background-size: 320px 44px !important; }

*:not([style*='background-image']):not([class*='arrow']):not([class*='gacha']) { background-size: cover; } #talk_window { top: 33px !important; } #tab, #tab3, #tab4 { display: table !important; }
.common_img, .common_img + div a[class*='flex-'] { background-size: 320px 388px !important; } .normal_img, .normal_img + div a[class*='flex-'] { background-size: 320px 1105px !important; }
.free_img, .free_img + div a[class*='flex-'] { background-size: 320px 799px !important; } .mix_img, .mix_img + div a[class*='flex-'] { background-size: 320px 501px !important; } .standing_gacha_hair_tab1, .standing_gacha_hair_tab2 { background-size: 160px 39px !important; position: relative; }

.button1, input[type='submit']:not([class^='event']):not([class*='red']), button[type='submit']:not([class*='red']) { background-image: linear-gradient(#FEDBEA 0%, #FFA5C8 50%, #FF8EB7 50%, #FF8EB7 100%); }
.button1.red, .button1.red_micro, .button1.red_s, .button1.red_m, .button1.red_l { background-image: linear-gradient(#FF9999 0%, #FF5778 50%, #FF3366 50%, #FF3366 100%); }

.hr-red, .hr-gacha-pink { background-image: linear-gradient(#fcd 0%, #fab 50%, #f89 50%, #f89 100%); } .hr-gacha, .hr-coution { background-image: linear-gradient(#f46 0%, #f46 50%, #e24 50%, #e24 100%); }
.hr-ok { background-image: linear-gradient(#b6cdfb 0%, #b6cdfb 50%, #82a7f1 50%, #82a7f1 100%); } .hr-aimi { background-image: linear-gradient(#9ed 0%, #9ed 50%, #6db 50%, #6db 100%); }
.help2, .help_event { background-color: #fff; }

.casino_body { background-size: 320px auto !important; } .img_casino_1 { background-size: 320px 995px !important; } .img_casino_2 { background-size: 320px 91px !important; }
.casino_button { box-shadow: 0px 0px 1px 2px #FAB225 inset; background-image: linear-gradient(#FFBE06 0%, #FFA104 50%, #FBA11B 50%, #E47E00 100%); }
.casino_button.ret, .event_page_button.casino { box-shadow: 0px 0px 1px 2px #020604 inset; background-image: linear-gradient(#117154 0%, #0C4C38 50%, #104C3A 50%, #0E231C 100%); }
.casino_button.ng { box-shadow: 0px 0px 1px 2px #000000 inset; } .event_page_button.casino.ng { box-shadow: 0px 0px 1px 2px #040404 inset; }

*[class*='assort_'], div[style*='balloon_mid'] { background-size:100% auto !important; } .assort_waku_m_bottom, .assort_waku_s_bottom { height: 42px !important; margin-bottom: 0px !important; } .assort_set_item { background-image: linear-gradient(#fdf9f8 10%, #ffcee4 70%, #fdd9db 100%); } .assort_tab { display: table !important; }

div[class*='bright'] { animation: bright 1s linear; animation-iteration-count: infinite; box-shadow: 0px 0px 6px 6px rgba(255, 255, 255, 1.0); }
@keyframes bright { 0% { opacity: 0.0; transform: scale(1.0, 1.0); background: #FFF; } 50% { opacity: 0.4; background: #FFF; } 100% { opacity: 0.0; transform: scale(1.1, 1.2); background: #FFF; } }
div[class*='bright'][class*='red'] { animation: bright-red 1s linear; animation-iteration-count: infinite; box-shadow: 0px 0px 6px 6px rgba(255, 0, 0, 1.0); }
@keyframes bright-red { 0% { opacity: 0.0; transform: scale(1.0, 1.0); background: #F00; } 50% { opacity: 0.2; background: #F00; } 100% { opacity: 0.0; transform: scale(1.1, 1.2); background: #F00; } }

div[class^='talk event'], div[class^='casino'][class*='talk'] { margin: -1.5px 0px !important; }
.bg_event_shop { background-image: linear-gradient(#ffe7ed 0%, #ffdee6 10%, #ffccd9 30%, #ffb6c8 70%, #ffa3ba 100%); }
.event_page_button { box-shadow: 0px 0px 1px 2px #dcd7d1 inset; background-image: linear-gradient(#85b936 0%, #85b936 50%, #52b82f 50%, #52b82f 100%); }
.event_button { box-shadow: 0px 0px 1px 2px #dcd7d1 inset; background-image: linear-gradient(#85b936 0%, #85b936 50%, #52b82f 50%, #52b82f 100%); }
.pickup { background-image: linear-gradient(#ffdf6d 0%, #febf3c 50%, #ffcb45 50%, #ff9e1c 100%); }
.raid_attack { box-shadow: 0px 0px 2px 3px #900000 inset; background-image: linear-gradient(#f20006 0%, #ce001b 50%, #ab0021 50%, #a20021 100%); }
.raid_attack.blue { box-shadow: 0px 0px 2px 3px #005070 inset; background-image: linear-gradient(#0d77a0 0%, #0d6790 50%, #0d5780 50%, #0d4770 100%); }
.bp_body { background-size: 100% 100% !important; } .bp_body.small { background-size: 60px 15px !important; }

.img_whiteday2017_1 { background-size: 120px 471px !important; } .img_whiteday2017_2 { background-size: 320px 517px !important; } .img_whiteday2017_2.header_line { background-size: 320px 6px !important; } .img_whiteday2017_3 { background-size: 90px 297px !important; } .img_whiteday2017_4 { background-size: 320px 458px !important; } .img_whiteday2017_5 { background-size: 179px 119px !important; }
.event_body.event_whiteday2017 { background-size: 320px auto !important; }

.img_hina_matsuri2017_1 { background-size: 120px 511px !important; } .img_hina_matsuri2017_2 { background-size: 320px 628px !important; } .img_hina_matsuri2017_3 { background-size: 185px 291px !important; } .img_hina_matsuri2017_4 { background-size: 320px 152px !important; } .img_hina_matsuri2017_5 { background-size: 218px 335px !important; }
.event_body.event_hina_matsuri2017 { background-size: 320px auto !important; }
}
