css:
@-moz-document domain("facebook.com") {
/* old code start */


body { 
background:url("http://static.wallpedes.com/wallpaper/facebook/facebook-world-map-wallpaper-wallpaper-facebook-world.jpg") no-repeat center fixed !important;
	background-size: cover;
        -webkit-background-size: cover;
        -moz-background-size: cover;
        -o-background-size: cover;
}

#jewelCase .jewel{float:left;margin-right: 10px;position:relative}
.jewelCount{
  background-color:#000000;
  top:-1px;
  color:#000000;
  font-size:9px;
  font-weight:bold;
  padding-bottom:1px;
  position:absolute;right:-1px;
  z-index:101;
  -webkit-border-radius:2px
}

.jewelCount span{
	background-color:#000000;
	padding:1px 1px 0;
	-webkit-border-radius:2px
	}

#pageNav._5bfj .tinyman img {
   border-radius: 30 !important; }
a[href*="find-friends"] { display:none!important; }


#background-container { 
position: fixed !important; 
top: 35px !important; 
}

#blueBarNAXAnchor {
  background: url("http://trm96.com/fb/bluebar15.png") repeat !important;
}

#blueBarHolder #blueBar { 
background: url("http://trm96.com/fb/bluebar15.png") repeat !important;
}
#leftCol { 
position: absolute !important;   
left: 0 !important;
padding:15px !important; 
width: 13% !important; 
background: #f0f0f0;
opacity:1.0;
border-radius: 60px 60px 60px 60px;  
border-right: 8px solid #3d5b99;
border-bottom: 8px solid #3d5b99;
border-left: 8px solid #3d5b99;
border-top: 8px solid #3d5b99;
-webkit-box-shadow: 0 0 20px #555 !important; 
}

#leftCol a { 
color: #222 !important; 
}

#leftCol a:hover { 
background: #8e8e8e  !important; 
}

.sideNavItem { 
padding: 4px !important; }

#stream_pagelet {
width: 100%; 
margin-left: 10px !important;
}

#rightCol {
  opacity: 0.9;
position: absolute!important;
  left: 75% !important;
  background: #f0f0f0;
  border-radius: 30px 30px 30px 30px;
  border-right: 8px solid #3d5b99; 
border-left: 8px solid #3d5b99;
border-top: 8px solid #3d5b99;
  border-bottom: 8px solid #3d5b99;
  }
.rhcFooterWrap { display: none !important; }

#contentArea { 
width: 69% !important;
margin-top: -10px !important;
margin-left: 8%;
opacity:1.0;
background: #f0f0f0;
border-radius: 30px 30px 0px 0px;
border-left: 8px solid #3d5b99;
border-right: 8px solid #3d5b99; 
border-top: 8px solid #3d5b99; 
-webkit-box-shadow: 0 0 20px #555 !important;
padding-top: 20px !important; 
} 
/* old code end*/

/* Start rewrite */

 .fbx #globalContainer {width: auto !important;}

#contentCol, .UIIntentionalStory_Message, .UIStory, .profile_color_bar, #pageFooter, .uiSideNav, #left_column, .profile_info_container, .profile-friends, #rightcolumn, .fbxWelcomeBox, .single_photo_header, .fbGroupedNotifications, .navMoreLess, .timelineUnitContainer{
 background-color:rgba(255, 255, 255, 0.0)
 !important;;
}


.UFIRow, .uiMorePagerPrimary, .uiSideHeader, .uiHeaderNav, .uiHeaderSection, .uiBoxWhite, .uiBoxLightblue, .uiBoxGray, .fbTimelineFeedbackActions {
background-color:rgba(255, 255, 255, 0.9) !important;
}
.uiStreamStory, .photocaption_text, .fbxWelcomeBox, .mbm:not(.uiHeader), .uiGrid, #pagelet_mutual, .timelineUnitContainer {
transition-property: background-color;
transition-duration: 0.3s;
}
.uiStreamStory:hover, .photocaption_text:hover, .fbxWelcomeBox:hover, .mbm:hover:not(.uiHeader), #pagelet_mutual:hover, .timelineUnitContainer:hover {
background-color:rgba(255, 255, 255, 0.9) !important;
transition-property: background-color;
transition-duration: 0.3s;
}

.uiHeaderTopBorder {
border-top: none !important;
}
.navHeader, .navHeader a {
color: #222222 !important
}


.fbTimelineSection:not(.fbTimelineTopSection), .timelineUnitContainer {
 background-color:rgba(255, 255, 255, 0.8)
 !important;
}
.fbTimelineScrubber, .fbTimelineTimePeriod {
background: none !important;
}
/* End rewrite */
}

@-moz-document domain("apps.facebook.com") {
#rightCol {display: none !important;}
#contentArea {opacity:1.0;}
.zap-zac-overlay .zap-zac-closable {display: none !important;}
}
