@-moz-document url-prefix("http://www.infinity-br.com/game/br1/") {

/* 基本フォントの色 */
body, table, td, span[style="color:white"], textarea.text_color1 {
  color: #333 !important;
  background-image: none !important;
}
/* 基本背景の色 */
body, table, td[bgcolor="#000000"], td[bgcolor="#080808"], table.win tr>td, textarea.text_color1 {
  background-color: #fff !important;
}
/* 背景画像があった場所の色 */
td.bgimg {
  background-color: #dde !important;  
  border: 1px #503 solid;
}
/* テーブルレイアウトの赤背景だった場所の色 */
th, table.ranking tr:first-child, table.win>tbody>tr:nth-child(-n+2)>td, *[bgcolor="#8b0000"] {
  background-color: #ede !important;  
}
/* テーブルレイアウトの枠線の色 */
.item th, .ste th, .ste td, .stinfo th, .stinfo td, .skill th {
  border: 1px #503 solid !important;
}
/* 黄色だった文字の色 */
font[color="yellow"], font[color="#ffff00"], font[color="#ffd700"], font[color="ffff00"], font[color="fff00"] {
  color: #660 !important;
}
/* アイコンの背景色 */
#statesmain td[rowspan="4"] {
  background-color: #647 !important;
}
/* 市場売買リスト項目欄のフォントの色 */
.skill th {
  color: #666 !important;
}
/* 地図の色 */
#map table td {
  background-color: #dde !important;
}
#map table td[bgcolor="#003366"] {
  background-color: #369 !important;
}
#map table td[bgcolor="#222222"] {
  background-color: #666 !important;
}
/* 生存者一覧とランキングのテーブルレイアウトの奇数行の色 */
table.ranking tr:nth-child(2n+2), table.ranking2 tr:nth-child(2n+3) {
  background-color: #eee !important;
}
/* 市場データベースのテーブルレイアウトの奇数行の色 */
table[width="860"] tr:nth-child(2n+1) td {
  background-color: #eee !important;
}
/* 市場データベースのテーブルレイアウトの偶数行の色 */
table[width="860"] tr:nth-child(2n+2) td {
  background-color: #fff !important;
}
  
.battle_info th {
  font-weight: normal !important;
}
table.win {
  border: 1px #333 solid;
}
table.gage td {
  border: none !important;
}
  
}
