css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ok.ru") {
#topPanel {
    margin-top: -60px;
    overflow-y: HIDDEN;
    transition-duration: .5S;
    transition-property: ALL;
    transition-timing-function: CUBIC-BEZIER(0, 1, 0.5, 1);
}

.notifications {
    padding-bottom: 30px;
}

.toolbar_c,.toolbar_nav,#topPanel:hover {
    margin-top: 0px;
    transition-duration: .5S;
    transition-property: ALL;
    transition-timing-function: CUBIC-BEZIER(0, 1, 0.5, 1);
}
}
