@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("primemusic.ru") {
html, body {background: url(http://ya-vidoc3000.narod.ru/anime/28.jpg) no-repeat fixed !important; -webkit-background-size: cover !important; background-size: cover !important;}
  p, span {background-color: white !important;}
  /**/
  #osx-placeholder, .banner_top, div.content-banner, .bot_shadow, #KI_14, div.menu_bottom, div.footer, div.partners, #vk_groups {display: none !important; visibility: hidden !important;}
  div.textblock {display: none !important;}
  div.menu {background-color: rgba(255, 0, 255, 1) !important; opacity: 0.7 !important; box-shadow: 0px 0px 30px 0px rgba(255, 0, 255, 1) !important;}
  /**/
  .alfavit {background-color: rgba(255, 0, 255, 1) !important; opacity: 0.7 !important; box-shadow: 0px 0px 30px 0px rgba(255, 0, 255, 1) !important;}
  div.toppp, div.bottt {background: none !important;}
  div.musicblock .caption {display: none !important;}
  div.musicblock .body {margin-top: -10px !important; margin-left: 10px !important; background: rgba(255, 0, 255, .7) !important;}
  table.best_tracks {background-color: rgba(255, 0, 255, 1) !important; font-size: 15px !important;}
  .best_tracks tr:nth-child(2n) {background-color: #32CD32 !important;}
  table.best_tracks td {border: none !important;}
  .comment_container {background-color: white !important; padding: 2px !important;}
  center, .other_track_link {text-align: left !important;}
  .like_table_left, .social {display: none !important;}
  a[href$=".mp3"] {background-color: red !important; padding: 5px !important;}
  /**/
  div.left-block .body li {padding-top: 3px !important;}
  div.left-block .body a {
  position: relative;
  display: inline-block;
  color: rgba(255, 0, 255, 1) !important;
  font-weight: bold;
  text-decoration: none;
  text-shadow: rgba(255,255,255,.5) 1px 1px, rgba(100,100,100,.3) 3px 7px 3px;
  padding: 1em 2em;
  outline: none;
  border-radius: 3px / 100%;
  background-image:
   linear-gradient(45deg, rgba(255,255,255,.0) 30%, rgba(255,255,255,.8), rgba(255,255,255,.0) 70%),
   linear-gradient(to right, rgba(255,255,255,1), rgba(255,255,255,0) 20%, rgba(255,255,255,0) 90%, rgba(255,255,255,.3)),
   linear-gradient(to right, rgba(125,125,125,1), rgba(255,255,255,.9) 45%, rgba(125,125,125,.5)),
   linear-gradient(to right, rgba(125,125,125,1), rgba(255,255,255,.9) 45%, rgba(125,125,125,.5)),
   linear-gradient(to right, rgba(223,190,170,1), rgba(255,255,255,.9) 45%, rgba(223,190,170,.5)),
   linear-gradient(to right, rgba(223,190,170,1), rgba(255,255,255,.9) 45%, rgba(223,190,170,.5));
  background-repeat: no-repeat;
  background-size: 200% 100%, auto, 100% 2px, 100% 2px, 100% 1px, 100% 1px;
  background-position: 200% 0, 0 0, 0 0, 0 100%, 0 4px, 0 calc(100% - 4px);
  box-shadow: rgba(0,0,0,.5) 3px 10px 10px -10px;
}
div.left-block .body a:hover {
  transition: .5s linear;
  background-position: -200% 0, 0 0, 0 0, 0 100%, 0 4px, 0 calc(100% - 4px);
}
div.left-block .body a:active {
  top: 1px;
}
  /**/
  ul.playlist span {background: none !important;}
}
