css:
@-moz-document domain("vozforums.com") {
/* Banh rộng  + xóa khung phải +  mở rộng tránh tên nick đụng time, bỏ scrollbar ngang) */
.page {min-width: 98%; background: #5C7099} body {margin:auto} TD[width="160"] {display:none}
[style*=wrap] > STRONG {margin-right:10px} 

/* Canh giữa hình, test: http://vozforums.com/showthread.php?t=2663169 */
[id*="ncode_imageresizer"],[id^="attachment"],
div[id*="post_message_"], [onload="NcodeImageResizer.createOn(this);"]:not([src*="vozforums.com/images/smilies"])
{margin-left: auto !Important; margin-right: auto !important; }
  
/* Fix vụ vỡ bố cục trang khi mở ảnh to , test : http://vozforums.com/showthread.php?t=3687819 */
.alt1 * {margin-right: auto ;margin-left:auto; font-size: 28px !important; line-height: 1.5!important;}  



/* Làm màu khung Reply */
.panel , .fieldset, .vBulletin_editor, .button, [id$=smiliebox] {background: #E4E7F5 !important; border:none !important}
.button {height: 30px} .button:hover, .imagebutton * {background: #F2F2FF !important} 
 
/* Banh rộng khung soạn thảo */
DIV[style="width:640px"], [type*="javascript"] + TABLE, #vB_Editor_001_textarea {min-width:100% }

.voz-post-message{font-size: 30px!important }
.alt2 {font-size: 28px!important; color:gray }
.vozlove-shortcut {font-size: 18px!important; color:white}
}

@-moz-document domain("otofun.net") {
.content , .message, .messagecontent, blockquote, .title * {font-size: 22px!important }
.mainContent {margin-right: 0px !important}
}

@-moz-document domain("danlambaovn.blogspot.com"), domain("danluan.org") {
.post * {font-size: 25px !important;  font-family: sans-serif!important;
line-height: 1.5;}
.field-item.even * {font-size: 30px !important; line-height: 1.2;}
}

@-moz-document domain("vi.wikipedia.org") {
div[id*=mw-content-text]{font-size: 22px !important;}
}

@-moz-document domain("bongdaso.vn") {
.art_content * {font-size: 23px!important; color:gray}
.right_pane {width:0px!important; display:none!important}
.left_pane, .left_block { width:960px!important; margin-right: 10px!important;}

._right_column_ {width:500px!important;}

.art_other_articles_list, .art_related_articles_list, .listnews_title * {font-size:16px!important}
h5{font-size:16px!important; padding-bottom:15px}
}

@-moz-document domain("hdvietnam.com") {
#content, #content_wrapper {margin-right: 0 !important}
.postrow * {font-size:25px!important; color:gray}
.threadtitle * {font-size:20px!important}
}

@-moz-document domain("news.zing.vn") {
.cms-desc.the-article-summary{font-size:20px}
.cms-body.the-article-body{font-size:20px}
}

@-moz-document domain("next.voz.vn"), domain("voz.vn") {
/* Khung tin */
.message-inner > .message-cell--main.message-cell {
    font-family: Arial!important;
    font-size: 25px!important;
    font-weight: 400;
    color: #5c5f5c;
    }

/* Khung Title List*/
.structItem-title {
    
    font-size: 25px!important;
    
    }

/* Khung Title Forum List*/
.js-nodeMain.node-main > .node-title {
    font-size: 25px!important;font-weight: 400!important;
    
    }

/* Khung Mess Qu*/
.bbCodeBlock-content {
    font-size: 22px!important;
    color:#828485;
    }


.block-container {background-color:#e8e8e8}
}
