@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("noscript.net") {

/* Remove Adverts */
.adhere, #google, #google2, .adblock, .sponsored, #ads0, .ad, .adb, .adc, #aw0, #aw1, #aw2, .ub0, .yell, .driverscan, #b_flashgot
{display:none !important}

/* Remove other crap */
#bsatop, .donation, #donation2, .signature, .copyright, .details-bar
{display:none !important}
}
