css:
@-moz-document domain("weheartit.com") {::-webkit-scrollbar {
  width: 20px;
  height: 20px;
}
::-webkit-scrollbar-button {
  width: 0px;
  height: 0px;
}
::-webkit-scrollbar-thumb {
  background: #2a2a2a;
  border: 0px none #ffffff;
  border-radius: 0px;
}
::-webkit-scrollbar-thumb:hover {
  background: #303030;
}
::-webkit-scrollbar-thumb:active {
  background: #2d2d2d;
}
::-webkit-scrollbar-track {
  background: #000000;
  border: 0px none #ffffff;
  border-radius: 0px;
}
::-webkit-scrollbar-track:hover {
  background: #080808;
}
::-webkit-scrollbar-track:active {
  background: #0d0d0d;
}
::-webkit-scrollbar-corner {
  background: transparent;
}

#adblock_warning{
   display: none !important;
}

html, body, .h4, .textfield, .searchfield, .textarea, .selectfield, .notification-is-unread, .panel-large, .btn-small, .btn-tag, .btn-mini, .list .list-item, .panel, .btn, input, textarea, select, button{
    background: #101010 !important;
    background-color: #101010 !important;
    font-family: Carrois Gothic, Arial Nova, Arial, sans-serif !important;
    color: #fff !important;
}

*{
    border-radius: 0px !important;
    box-shadow: none !important;
}



.tabs-light>li>a, .text-primary, hr{
    color: #fff !important;
}

.icon, .col .img-static, .cel .img-static, .grid-item .img-static, .form-label .img-static, .form-input .img-static{
    opacity: 1 !important;
    -webkit-filter: brightness(500%) ;
}

.bg-primary, .inspiration-preview .inspiration-name, .bg-offwhite, ul.tabs-light, #navbar{
    background: #000000 !important;
}

.position-fixed, #infinite-nav, .dropdown-right .dropdown-menu-light{
    background: rgba(0,0,0,150) !important;
    border-bottom: 3px solid #000000 !important;
}


.grid-thumb .entry, .grid-item img, .inspiration-preview{
max-width:322px !important;
max-height: 340px !important;
margin: 0px !important;
padding: 0px !important;

}


.panel-small, .btn-mini{
   opacity: 0 !important;
   background: #000 !important;
   transition: ease-in-out 1000ms !important;
}

.panel-small:hover, .btn-mini:hover{
   opacity: 1 !important; 
}

.panel-small{
 display: none !important;
}


.btn-heart-circle, .header-cover:after{
    background: transparent !important;
}

.entry-preview .entry-actions{
    border-top: 2px solid #fff !important;
}

a.js-share-button.col{
    border-right: 2px solid #fff !important;
}

.entry-preview .entry-hover:before{
   background: rgba(0,0,0,0.7) !important;
   transition: ease-in-out 1050ms !important; 
}

body > div.modal.js-modal-container > div > form > div > div:nth-child(2) > div > div.col.span-8 > div{
    display:block !important;
    opacity: 1 !important;
}
input[type="search"]:hover,body > div.modal.js-modal-container > div > form > div > div:nth-child(2) > div > div.col.span-8 > div > div:nth-child(1) > input{
    background: black !important;
    color: white !important;
    border: 1px white solid !important;
}

}
