css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("camptocamp.org") {

html, body, #holder, .pun {
  min-width: 800px !important;
  font: 13px/1.5 "Droid Sans", sans-serif !important;
  background-color: #fff !important;
  color: #000 !important;
}

/* GLOBAL STYLES */

pre, code {
  font-family: "Droid Sans Mono", monospace !important;
  font-size: 0.95em !important;
}

q { quotes: '«' '»'; }
q:before { content: open-quote "\0000a0"; }
q:after { content: "\0000a0" close-quote; }

blockquote, div.codebox {
  margin: 1em !important;
  background-color: #f9f8f5 !important;
  padding: 1em !important;
  border: 1px solid #ddd !important;
}

input, select, textarea {
  font-family: "Droid Sans Mono", monospace !important;
  background-color: #eee;
  border: 1px solid #ddd;
  font-size: 12px !important;
}
input {
  -moz-border-radius: 8px;
  -webkit-border-radius: 8px;
  border-radius: 8px;
}
input:hover { background-color: #dedede !important; }

.htext, .article .htext {
  margin: 0.5em 0 0;
  padding: 0 !important;
  font-family: "Droid serif", serif !important;
}
h4, h2.htext { font-size: 1.25em !important; }
h5, h3.htext { font-size: 1.1em !important; }
h6, h4.htext { font-size: 1.1em !important; }

a, a:hover {
  border-bottom: 1px dotted #666 !important;
  text-decoration:none !important;
}
a:hover {
  color: #f90 !important;
  border-bottom:1px dotted #f90 !important;
}
a.link_nav_anchor, a.nav_edit {
  padding-left: 22px !important;
  color: #666 !important;
}
a.link_nav_anchor:hover, a.nav_edit:hover { color: #f90 !important; }

.htext a, #footer a, .add_content a, a.link_nav_anchor, a.nav_edit, .toc a, #menu_content a, #menu_content a:hover, #log a, #log a:hover, .latest li a, #data_section_container a, a.qck_sw, .image a, #hoverNav a, #imageDataContainer a, .box a {
  border: 0 !important;
}

span.hextra, .article span.hextra {
  color: #686868 !important;
  margin-left: 1em !important;
  font-style: normal !important;
}
span.hextra:before { content : "("; }
span.hextra:after { content : ")"; }

.switch_lang {
  position: relative;
  top: -1ex;
  float: right;
  right: 1em;
}
.switch_lang div:first-child {
  display: none;
}
.switch_lang a, div.current_lang {
  width: 2ex;
  overflow: hidden;
  display: inline-block !important;
  border-bottom: 1px dotted #686868 !important;
}
div.current_lang {
  font-weight: normal !important;
  color: #f93 !important;
}

/* REMOVE STUFF */

#path, .ombre_haut, .ombre_bas, #nav_buttons, #wizard_button, #nav_space, #pub, #nav_anchor, .translate_button, #menu_border_left, #menu_border_right, #splitter, #footer_border_left, #cc, #footer_border_right, #footer_cc_text > form, .picto_open, .picto_close {
  display:none !important;
}

/* GLOBAL LAYOUT */

#header, #menu, #content_box, #footer, #punwrap {
  margin: 0 auto !important;
  padding: 0 !important;
  width: 80% !important;
  min-width: 900px;
}

#content_box, #punwrap > div {
  float: none !important;
  position: relative;
  border: 1px solid #ddd;
  border-width: 0 2px;
  width: 80% !important;
  -moz-box-shadow: 0 0 10px #ccc;
  -moz-border-radius: 8px !important;
  -webkit-border-radius: 8px !important;
  border-radius: 8px !important;
}

#wrapper_context, #article {
  padding: 20px !important;
  border: 0 !important;
}

#punwrap > div {
  padding: 20px !important;
  width: auto !important;
}

.clearing, #punviewtopic .clearing {
  padding: 20px 20px 0 !important;
}

/* HEADER */

#header {
  position: relative;
  height: 70px !important;
}
#header a:hover { border-bottom: 1px dotted #f90 !important; }

#banner_logo {
  background: transparent !important;
  top: 10px !important;
  left: 0 !important;
  text-transform: uppercase;
  font-weight: bold;
  font-size: 1.4em;
  height: auto !important;
  width: auto !important;
  text-shadow: 1px 1px 10px #CCCCCC;
  }
#banner_logo:before {
  content : "Camptocamp";
  color: #f90; font-size: 2em;
}
#banner_logo:after {
  content : ".org";
}

#banner_middle {
  margin: 0 !important;
  height: auto !important;
  position: absolute !important;
  top: 0.5em;
  right: 0;
  font-size: 0.95em !important;
  z-index: 1 !important;
}

#log > div { float: right; padding-left: 20px !important; }
#log > form { position: absolute; right: 0; top: 30px; }

.sbox {
  border:1px solid #ddd;
  -moz-border-radius: 8px !important;
  -webkit-border-radius: 8px !important;
  border-radius: 8px !important;
}
.sbox select {
  width: 20px !important;
  border: 0 !important;
  margin: 0 0 0 8px !important;
  padding-left: 25px !important;
}
.sbox input {
  width: 250px !important;
  margin: 0 !important;
  padding: 2px !important;
  border-color: #fff !important;
}
.sbox option {
  margin: 0 0 0 5px !important;
  padding-left: 25px !important;
  width: 225px !important;
}
input.action_filter { background: transparent !important; }

a.logged_as { background: transparent !important; padding: 0 !important; }
.action_cancel { background: transparent !important; height: auto !important; }
.log_elt .action_cancel:after { content : "✘"; }

/* MENU */

#menu {
  margin-bottom: 40px !important;
}

#menu_up {
  position: relative;
  border: 1px solid #ddd !important;
  background-color: #eee !important;
  -moz-border-radius: 8px 8px 0 0 !important;
  -moz-box-shadow: 0px 1px 6px #ddd;
}

div#quick_switch {
  left: auto !important;
  right: 0 !important;
  padding: 0 !important;
}

#menu_content {
  padding: 0 5px !important;
  color: #f90 !important;
}

#menu_content li {
  background-color: transparent !important;
  padding-right: 1ex !important;
}
#menu_content ul ul {
  background-color: #eee !important;
  border: 1px solid #ddd !important;
  padding: 0 !important;
  margin-left: 0 !important;
  width: 100% !important;
  height: 2em;
  left: 0;
  overflow: hidden;
  -moz-box-shadow: 0 2px 6px #ddd;
}
#menu_content li li {
  padding: 0 !important;
  display: inline-block !important;
  height: 1.7em;
  overflow: hidden;
}
#menu_content ul ul ul {
  display: none !important;
  }

#menu_content a {
  color:#f90 !important;
  height: auto !important;
  }

#menu_content li a {
  padding-right: 5px !important;
}
#menu_content ul li a {
  text-transform: uppercase !important;
  }
#menu_content li li a {
  color: #555 !important;
  padding: 0 0 0 24px !important;
  height: auto !important;
  line-height: 26px !important;
  font-weight: normal !important;
  text-transform: none !important;
  }
#menu_content ul ul a:hover {
  background-color: transparent !important;
  color:#f90 !important;
  }

/* HOME */

.home_article a.picto_rss, .home_article span.picto_rss, #last_images_section_title, .home_link_list a[href*="/forums/?lang="], .home_link_list a[href$="feed.rss"], .home_link_list a[href$="/articles/107228/fr"], .home_link_list a[href$="/users/customize"] {
  display: none !important;
}

.home_article {
  margin: 0 0 150px !important;
  color: #444 !important;
}
.home_article li a { color: #444 !important; }
.home_article li a:hover { color: #f93 !important; }

#home_left_content, #home_right_content {
  margin: 0 !important;
  padding-top: 0 !important;
}
#home_left_content { width: 65% !important; }
#home_right_content { width: 33% !important; }

.home_title {
  height: auto !important;
  background-color: transparent !important;
}
.home_title_text {
  padding: 5px !important;
  margin: 0 5px 0 5px !important;
  font-family: "Droid serif",serif !important;
  border-bottom: 2px solid #ddd !important;
  font-size: 1.6em !important;
}

.home_title_text a, .home_title_right a { border-bottom: 0 !important; }

.home_link_list {
  color: #fff !important;
  display: inline !important;
  margin: 0 !important;
}
.home_link_list a {
  font-size: 11px;
  border: 1px dotted #ddd !important;
  background-color: #f1f1f1;
  padding: 2px 3px !important;
  -moz-border-radius: 8px;
  -webkit-border-radius: 8px;
  border-radius: 8px;
}
.home_link_list a:hover {
  background-color: #eaeaea;
}

.home_title_left {
  margin: 8px 8px 0 5px !important;
  top: 2px !important;
}

ul.recent-changes li:hover a { border-bottom:1px dotted #f90 !important; }
ul.recent-changes li:hover, ul.recent-changes li:hover a { color:#f90 !important; }
ul.recent-changes .meta { position: absolute; right: 0; }
ul.recent-changes li.even { background-color: #FAFAFA !important; }
#last-outings ul.recent-changes .meta { margin-right: 25px !important; }

#edit_me {
  background-color: #fafafa !important;
  border: 2px solid #ddd;
}

#last_images {
  position: absolute;
  bottom: 0;
  margin: 0 auto !important;
  }
#last_images #image_list { height: 145px !important; }

#on_the_web_section_container { display: block !important; }

ul.dated_changes li.even, ul.listed_changes li.even,
ul.article_changes li.even, ul.docs_changes li.even {
  background-color: transparent !important;
}

.latest li {
  border-bottom: 1px dotted #dedede;
}
.latest li:hover, .latest li.even:hover {
  background-color: #f7f7f7 !important;
}

.dated_changes .meta a {
  color: #666 !important;
  text-decoration: none !important;
  border-bottom: 0 !important;
}

#last_articles li {
  list-style-image: url(http://www.camptocamp.org/static/images/picto/puce2.png) !important;
  margin-left: 20px !important;
}

/* SIDEBAR */

.nav_box_top, .nav_box_down, #nav_tools_top, #nav_tools_down, #nav_prepare, #nav_share { display: none !important; }

.nav_box {
  width: 17% !important;
  padding: 20px 10px 0 20px !important;
  margin: 0 !important;
  border-right: 2px solid #eee;
}

.nav_box_content {
  background: transparent !important;
  width: 100% !important;
  border: 0 !important;
}
.nav_box_text, .nav_box_title {
  margin: 0 0 5px !important;
  padding: 0 !important;
}
.nav_box_bottom_link { text-align: left !important; margin: 0 !important; }
.nav_box_content a { font-weight: normal !important; color: #444 !important; }
.nav_box_image { float: left; }
.nav_box_title_text { margin: 0 0 0 20px !important; }

#nav_about {
  padding-top: 0 !important;
  margin-top: 20px !important;
}

/* TOOLBAR */

#nav_tools {
  position: fixed;
  bottom: 10px;
  left: 10px;
  z-index: 50;
  width: 180px !important;
  height: 280px !important;
  margin: 0 !important;
  padding: 1em !important;
  border-right: 0 !important;
  }
#nav_tools:hover {
  background-color: #eee;
  border: 1px solid #ddd !important;
  -moz-border-radius: 8px !important;
  -webkit-border-radius: 8px !important;
  border-radius: 8px !important;
  /* -moz-box-shadow: 1px 0 10px #ccc; */
}
#nav_tools_content {
  background-color: transparent !important;
  width: 100% !important;
}

#nav_tools ul { position: absolute; top: -10000px; }
#nav_tools:hover ul { position: absolute; top: 1em; }
#nav_tools ul li {
  width: 180px !important;
  display: block !important;
}

/* DOC EDIT */

#nav_edit {
  font-size: 11px;
  margin: 0 0.5em !important;
  width: auto !important;
  float: right !important;
  position: absolute;
  right: 0;
  top: -42px;
  border-right: 0 !important;
  }

#nav_edit li {
  display: inline-block;
  padding: 2px 8px !important;
  margin-left: 5px !important;
  background-color: #fafafa;
  -moz-box-shadow: 1px 0px 5px #eee;
  border: 1px solid #ddd !important;
  border-width: 1px 1px 0 !important;
  -moz-border-radius: 5px 5px 0 0;
}
#nav_edit li.active_tab, #nav_edit li:hover {
  background-color: #eee;
  width: auto !important;
}

#nav_edit li a, #nav_edit li div {
  font-weight: normal !important;
  color: #555 !important;
  border-bottom: 0 !important;
  margin: 0 !important;
  font-size: 11px !important;
  }
#nav_edit li a:hover {
  border-right: 0 !important;
  }
#nav_edit li.active_tab a {
  font-weight: bold !important;
}

#nav_edit li a:link, #nav_edit li a:visited, #nav_edit li div {
  background: transparent !important;
  display: inline !important;
  padding: 0 !important;
  }
#nav_edit li a.action_comment { font-size: 11px !important; }
#nav_edit li a.action_comment span.reduced { display: none !important; }

/* TOPO */

#tip_data, #tip_map_container, #tip_description, #tip_images, #tip_outings, #tip_associated_docs, #tip_view, #_description, .action_toggle, .outings_content #map_container_section_container {
  display: none !important;
  }

/* #article .article_titre_bg:first-child,
.outings_content .article_titre_bg:first-child + div + div */

.article_title {
  color: #686868 !important;
  font-family: "Droid Sans", sans-serif !important;
  padding-left: 40px !important;
  border-bottom: 1px dotted #f90 !important;
  }
.article_title_img {
  margin: 10px 10px 0 0 !important;
}

.content_article .article {
  border: 0 !important;
  margin-left: 0 !important;
  margin-right: 0 !important;
  padding: 0 !important;
}

#article a { border-bottom: 1px dotted #686868; color: #686868; }
#article a:hover { border-bottom: 1px dotted #f90; color: #f90 !important; }
.title, .title a {
  margin-left: 0 !important;
  font-size: 1.2em !important;
  font-weight: normal !important;
  border-bottom: 0 !important;
  }

.article_titre_bg {
  border-bottom: 1px dotted #C7C7C7 !important;
  height: 28px !important;
  }

#content_article .section_subtitle, #article_infos_titre, .article_infos_titre {
  font-weight: normal !important;
  z-index: 1 !important;
  }
.article_contenu > .section_subtitle {
  font-size: 1.4em !important;
  }

#license_box {
  padding: 0 !important;
  margin: 1em 0 4em !important;
  }

span.toc_link a, #article span.toc_link a {
  font-size:0.5em !important;
  border: 1px solid #ccc !important;
  float: right;
}

.article .linkst { float: none !important; }
#article_infos_titre_contenu, .article_infos_titre_contenu { text-align: left !important; }

#data_section_container div.all_associations,
#data_section_container ul.data { margin: 0 !important; }

#data_section_container { padding: 1em 0 !important; }

#map_container .default_text { display: none !important; }

/* LISTS */

table.list, table#pagehistory {
  width: 100%;
  border: 1px solid #ccc !important;
}

tr.table_list_odd {
  background-color: #f7f7f7 !important;
}

table.list th, table#pagehistory th {
  background-color: #eee !important;
  border-right: 1px solid #ddd !important;
}

/* ARTICLES */

table.toc {
  float: right !important;
  margin: 0 0 1em 1em !important;
  position: relative !important;
  z-index: 100 !important;
  width: auto;
  max-width: 400px;
}
/*
.toc a span {
  color: #686868 !important;
  font-weight: normal !important;
}
.toc a span:hover { color: #f90 !important; }
*/

.articles_content .htext, #article.articles_content .htext {
  margin: 1em 0 !important;
}
.articles_content h2.htext {
  border-top: 0 !important;
  border-bottom: 1px solid #E0E0E0 !important;
  text-transform: uppercase;
}
#article h2 a { border: 0 !important; }
.articles_content h3.htext {
  border-bottom:1px dotted #aaa !important;
  padding:0.2em 0 !important;
}
.articles_content h4.htext, .articles_content h4.htext a, .articles_content h5.htext, .articles_content h5.htext a {
  font-style: italic !important;
  font-weight: normal !important;
}

/* PUNBB */

.pun, .pun optgroup {
  font-family: "Droid Sans", sans-serif !important;
}

#brdwelcome, div.block, div.blockform, div.block2col, #postreview, div.blocktable, div.inbox {
  margin: 0 !important;
  padding: 0 !important;
  }

.inbox a, .inbox a:visited, .postright a:visited, .blocktable a:visited, #punmovepost a, #punmovepost a:visited {
  border-bottom: 1px dotted #666 !important;
  color: #666 !important;
  }
.pun a:visited {
  color: #999 !important;
}
.inbox a:hover, #punmovepost a:hover {
  border-bottom: 1px dotted #f90 !important;
  color: #f90 !important;
}
#vf a, .postquote a, .postquote a:visited, .postreport a, .postreport a:visited {
  border-bottom: 0 !important;
}

td div.tclcon { font-weight: normal !important; }
.inew { font-weight: bold !important; }

div.box, .rowodd, .roweven { background: transparent !important; }

#punredirect { margin-top: 200px; }

.pun h2, #brdmenu {
  color: #f90 !important;
  background: transparent !important;
  margin: 1em 0 0.5em !important;
  padding: 0 !important;
  }
.pun h3 { font-size: 1.2em !important; }
.pun h4 { font-size: 1em !important; }

.conl, .conr { padding: 0 !important; }
ul.conr li { float: right; margin-left: 1em !important; }
ul.conr li:first-child+li+li+li { display: none; }

/*#punviewforum h2 { border-bottom: 1px dotted #f90; }*/

div.blockpost h2 { background-color: transparent !important; font-size: 1em !important; font-weight: normal !important; }
div.blockpost h2 span.conr { color:#666 !important; }
div.blockpost div.box { border: 1px solid #FFBA4F !important; }
div.blockpost label { background-color:#f7f7f7 !important; }
div.blockpost > div.box, div.blockpost > div > div.inbox { background-color:#f7f7f7 !important; padding-bottom:0 !important; }

div.linkst, div.linksb, div.postlinkst {
  margin: 1em 0 !important;
  padding: 0 !important;
}
.linkst ul, .linksb ul, .postlinkst ul, .postlinksb ul {
  padding: 0 !important;
  margin-left: 1em !important;
  float: left;
  clear: none !important;
}
div.linkst .conl, div.linksb .conl, div.postlinkst .conl, div.postlinksb .conl {
  width: auto !important;
}

.pun th { background: #eee !important; border: 1px solid #ccc !important; }
.pun td { border: 1px solid #ccc !important; }

td.tcl, td.tcr{ background: #fafafa !important; }
td.tc2, td.tc3, td.tcmod, #postpreview, #viewprofile dd, div.forminfo, #adminconsole fieldset td, div.blockmenu div.box, #adstats dd { background-color: #eee !important; }

div.postleft, div.postfootleft { background-color: #fff !important; }
div.postright, div.postfootright { background-color: #f7f7f7 !important; border-left-color: #fff !important; }

div.postmsg { min-height:8em !important; }

.usertitle { color: #999 !important; }

.pun div > fieldset { border: 0 !important; }
.pun legend { color: #999999 !important; padding:0 !important; }
div.blockform form, div.fakeform { padding: 10px !important; }
div.blockform div.box { border-color: #FFBA4F !important; background-color: #f7f7f7 !important; }
div.inform { padding: 0 !important; }
div.txtarea { width: 95% !important; }
div.infldset > div { float: left !important; margin: 2px 5px !important;}
ul.bblinks { padding: 2px 0 !important; }
#postreview h2 { font-size: 1.5em !important; margin: 5px 0 !important;}
#postreview div.box { border: 1px solid #ccc !important; }

div.postsignature hr { background-color: #aaa !important; }

#punviewtopic .article_title { padding-left: 35px !important; }

/* FOOTER */

#footer {
  background: transparent !important;
  margin-top: 0.5em !important;
  padding: 10px 0 !important;
  height: auto !important;
  color: #aaa !important;
  font-size: 10px !important;
  text-transform: uppercase !important;
  left: auto !important;
}
#footer a {
  color: #f90 !important;
  font-weight: bold !important;
  }

#footer_cc {
  margin: 0 !important;
  position: static !important;
  }
#footer_cc_text {
  width: 70% !important;
  padding-left: 10px !important;
  }
#footer_cc_text > p+p { display: inline !important; }
#footer_cc_text > p+p:after { content : " | "; }

#footer_partners {
  right: 10px !important;
  left: auto !important;
  text-align: right;
  }
#partners { float:right; }

/* LOGIN */

#MB_window {
  position: absolute !important;
  top: 0 !important;
  bottom: 0 !important;
  left: 0 !important;
  right: 0 !important;
  margin: auto !important;
}
#MB_frame { padding: 7px 0 !important; }
#MB_header { border-top: 1px solid #CCCCCC; }
#MB_close { top: .3em !important; }

/* CHANGER D'APPROCHE */

#cda_logo { margin: 35px 0 0 !important; }
#cda_title { margin: 20px 0 0 !important; }
#cda_sub_title { margin: 0 0 10px !important; }
#c2c_mw_logos { display: none !important; }

}
