css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("changelog.com"), domain("thechangelog.com") {
  body {
    background-color: #272727;
  }
    
  .popular-tags {
    background: none;
    background-color: #373737;
    color: #a8a8a8;
  }
  
  .popular-tags a {
    color: #a8a8a8;
    text-decoration: none;
  }
  
  .header {
  	background: none;
  }

  .header .contains-img {
  	display: block;
  	-moz-box-sizing: border-box;
  	box-sizing: border-box;
  	background: url(http://raw.githubusercontent.com/thechangelog/nightly/master/images/logo-night.png) no-repeat;
    -webkit-background-size: 100% auto;
    -moz-background-size: 100% auto;
    background-size: 100% auto;
  	width: 150px;
  	height: 66px;
  }

  .header .contains-img img {
  	display: none;
  }

  .page-wrapper {
  	background-color: #272727;
  }
  
  .page-wrapper h2, .page-wrapper h2 a,
  .page-wrapper h1, .page-wrapper h1 a {
    color: #fff;
    border-bottom: none;
  }
  
  .page-wrapper h2 a:hover,
  .page-wrapper h1 a:hover {
    border-bottom: none;
  }
  
  .page-wrapper p {
  	color: #a8a8a8;
  }

  .page-wrapper a {
    text-decoration: none;
  	color: #fff;
  }

  .page-wrapper em,
  .page-wrapper blockquote,
  .page-wrapper mark {
  	background-color: #373737;
  }

  .page-wrapper a:hover {
  	color: #428BCA;
    background: none;
  }
  
  #footer {
    color: #a8a8a8;
  }
  
  #footer a {
    color: #fff;
  }
}
