css:
/* @name Dark/Night Choice of Games */
@-moz-document url-prefix("https://www.choiceofgames.com"),
url-prefix("http://www.choiceofgames.com"){
body, html {
	background: #1e1e1e !important;
	color: #cacaca !important;  
}

a:link {
	color: #cacaca;
}

a:visited {
	color: #666;
}

.alertify-dialog {
	background: #1e1e1e !important;
	color: #cacaca !important;  
}

#feature, #roundShell {
	background: #2e2e2e !important;
}

.gameItem {
	background: #3e3e3e;
}

.buyIt {
	border: 1px solid #323232;
}

.buyIt .main {
	background: url('http://i.imgbox.com/SzumvSDr.png') no-repeat;
	background-color: #363636;
}

.buyIt .main .subnav li {
	border-top: 1px solid #323232;
}

.buyIt .main .subnav li:hover, .buyIt .main .subnav li:hover:last-child {
	background: #C77A26;
}

.widget {
	background: none;
}

label {
	color: #cacaca !important;
}

#menu-top-navigation li.current-menu-item a {
	color: #cacaca;
	background: #2e2e2e;
}

#menu-top-navigation li a:hover {
	color: #cacaca;
	background: #2e2e2e;
}

img[src="https://www.choiceofgames.com/wp-content/themes/choiceofgames/images/logo.png"] {
	height: 0px;
	width: 0px;
	padding: 43px 250px 43px 0px;
	background: url("http://i.imgbox.com/OrSDQqBv.png") !important;
}

.choice label {
	border-color: #5e5e5e;
}
.choice label:hover {
	background-color: #2e2e2e;
}
}
