css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("golem.de") {
    /*Navigation-bar uses lesser space and improved*/
    /*DroidSerif,Arial,sans-serif*/
    
    * {
        font-family: "Times New Roman", Times, serif !important;
    }
    .cluster-header p {
        line-height: 1.25em;
    }
    #bannerTopWrapper, #iqadtile4, #rl_afcbeco1b, #iqdSitebar, #iqadtile8, .cluster-header div {
        display: none !important;
    }
    .golemContentToHide, .article {
        background-color: #FFF !important;
    }
    @media all and (max-width: 70em) {
        #grandwrapper > header > nav li #grandwrapper > footer > nav li {
            min-width: 25% !important;
            max-width: 33.333% !important;
            max-height: 1.8em;
            float: left !important;
            text-transform: none !important;
            margin-right: -0.1em !important;
            box-shadow: 0 0 0 3px grey !important;
        }
        #grandwrapper > header > nav li {
            max-width: 33.333% !important;
            min-width: 25%;
            width: auto !important;
            position: relative !important;
        }
        @media all and (max-width: 35em) {
            #grandwrapper > header > nav ul, #grandwrapper > footer > nav ul {
                position: relative !important;
                margin-right: -0.1em !important;
            }
            #grandwrapper > header > nav li,
            #grandwrapper > footer > nav li {
                min-width: 100% !important;
                /* <- that makes no sense, but i had to overwrite the parameter*/
                
                border-top: 0.1em inset gray;
                border-bottom: 0.1em inset gray;
                margin: -0.05em 0 !important;
            }
            #grandwrapper > header > nav li:nth-child(n+4) {
                margin: -0.1em 0 !important;
            }
            #grandwrapper > footer > nav li {
                min-width: 50% !important;
            }
            #grandwrapper > footer > nav li:nth-child(1) > a,
            #grandwrapper > footer > nav li:nth-child(2) > a {
                margin: 0 0 -0.05em;
                max-height: 3em !important;
            }
            #grandwrapper > footer > nav li:nth-child(3):last-of-type {
                /* <- workaround*/
                
                width: 100% !important;
                display: block !important;
                margin-bottom: -0.1em !important;
            }
        }
        #grandwrapper > header > nav li:hover {
            text-shadow: 0px 0px 0.1em;
        }
        #grandwrapper > footer > nav li:hover {
            text-shadow: 0px 0px 0.1em;
        }
        #grandwrapper > header > nav li:active {
            background-color: #282828 !important;
        }
        #grandwrapper > footer > nav li:active {
            background-color: #282828 !important;
        }
        #grandwrapper > footer > nav,
        #grandwrapper > header > nav {
            font-size: 110% !important;
        }
        #grandwrapper > footer > nav a,
        #grandwrapper > header > nav a {
            padding: 0 !important;
            height: 100% !important;
        }
        /*footer too*/
        
        #grandwrapper > footer > div.meta {
            padding: 0.4em 1.5em 0.2em !important;
        }
        #grandwrapper > footer > nav {
            width: 100% !important;
        }
        #grandwrapper > footer > nav li {
            width: 25% !important;
        }
        .asleft {
            margin: -1em 0;
        }
        .asleft > ul {
            padding: 0.5em 0 !important;
        }
        /*format font*/
        
        .head-list > * {
            display: none !important;
        }
        .leader .head-main {
            font-size: 1.25em !important;
            padding-bottom: 0.4em !important;
            line-height: 1em !important;
            font-weight: bold;
        }
        .leader {
            border-bottom: 2px solid lightgray;
        }
        .leader p {
            font-size: 1.3em !important;
            line-height: 1.2em;
        }
        .list-articles {
            margin: -0.5em auto !important;
        }
        .list-articles p {
            font-size: 1.25em !important;
            line-height: 1.2em;
        }
        .list-articles > li .head-main {
            font-size: 1.25em !important;
            padding-bottom: 0.4em !important;
            line-height: 1em !important;
            font-weight: bold;
        }
        .list-articles img {
            top: 1.5em;
        }
        h1,
        h2,
        h3,
        h4,
        h5,
        h6 {
            font-size: 1.25em !important;
            line-height: 1.1em;
        }
        .head-main,
        .head5 {
            font-weight: bold;
            line-height: 1em;
            margin-top: 0.5em !important;
        }
        .formatted {
            padding-bottom: 0.5em;
            border-bottom: 1px solid lightgrey;
        }
        .formatted p,
        .formatted ol,
        .formatted ul {
            font-size: 1.25em !important;
            line-height: 1.3em;
            word-wrap: break-word !important;
        }
        .cluster-header p {
            font-size: 1.35em !important;
            font-weight: bold;
            line-height: 1.2em;
            margin-bottom: 0.5em !important;
            border-top: 1px solid lightgrey !important;
            padding-top: 0.5em !important;
        }
        .cluster-header .head-meta,
        .cluster-header .head1 {
            font-size: 1em !important;
        }
        .list-ticker li {
            padding-left: 0px !important;
            font-size: 0.8em !important;
        }
        .list-pages {
            float: left !important;
            font-size: 110% !important;
        }
        .comment-count {
            font-size: 1.1em;
        }
        .social-bar,
        .social-bar a {
            display: none;
        }
        .article aside li {
            margin: -1.5em 0 !important;
            border-bottom: none !important;
        }
        .article aside ul {
            padding: 1.3em 0 0.7em !important;
        }
        .article article .link-comments {
            margin: -3em 0 -0.4em !important;
            padding-top: 0.5em !important;
            font-size: 1.1em !important;
        }
        /*forum list*/
        
        .forum .desc {
            display: none;
        }
        .forum .list-comments li:first-child {
            border-top: 1px solid lightgrey;
            border-bottom: 1px solid lightgrey;
        }
        .forum .list-pages:nth-of-type(2) {
            display: none !important;
        }
        .forum .list-pages:nth-of-type(3) {
            padding: 0 1em 0 !important;
            margin: 2.85em 0 !important;
            float: right !important;
            position: relative !important;
        }
        .forum .list-pages:nth-of-type(4) {
            float: right !important;
            margin: -0.8em 0 -0.85em !important;
        }
        .list-comments li {
            border-bottom: 1px solid lightgrey;
        }
        .forum .list-comments h3 {
            border-bottom: 1px dotted lightgrey !important;
        }
        /* <experimental

.list-comments h2, h3 {

  background-color: #F0F0F0 !important;

  margin: -0.7em -0.5em 0 0 !important;

  padding: 0.7em 0.5em 0 0 !important;

}

                                                  /experimental> */
        
        .list-comments li:hover {
            background-color: #F0F0F0 !important;
        }
        .list-comments li:link {
            background-color: #F0F0F0 !important;
        }
        .link-comments .list-comments li:visited {
            background-color: #F0F0F0 !important;
        }
        .list-comments li:hover {
            background-color: #F0F0F0 !important;
        }
        .list-comments li:active {
            background-color: #F0F0F0 !important;
        }
        .contains-comments .head-main {
            margin-top: 0em !important;
            font-size: 1.25em !important;
        }
        .contains-comments .count {
            font-size: 1.5em !important;
        }
        .forum .inputCheckbox {
            height: 1em !important;
            width: 1em !important;
        }
        /*forum itself*/
        
        .forum #screen > .head-main {
            display: none;
        }
        .forum div.information {
            margin-top: 2em !important;
        }
        .list-breadcrumbs {
            margin: -0.6em 0 0.6em 1em;
            width: auto !important;
            max-height: 1.5em !important;
            overflow: scroll !important;
            text-overflow: ellipsis !important;
            white-space: nowrap;
        }
        .list-breadcrumbs li {
            font-size: 1em !important;
            margin-bottom: -0.3em;
        }
        .forum #pre-thread,
        .forum #post-thread {
            font-size: 1em !important;
            font-weight: bold;
            margin: 0 -10em -0.4em;
            width: auto !important;
            max-height: 1.5em !important;
            overflow: scroll !important;
            text-overflow: ellipsis !important;
            white-space: nowrap;
        }
        .forum #pre-thread > a,
        .forum #post-thread > a {
            margin: -0.7em 0 -0.7em;
            padding: 0.5em;
        }
        .forum #pre-thread:nth-of-type(1) {
            padding: 0.2em 0.5em 0.8em;
            float: right;
        }
        .forum #pre-thread:nth-of-type(2) {
            display: none !important;
        }
        .forum #post-thread {
            float: right !important;
            margin: -0.6em 0.3em -0.3em !important;
        }
        .msg-body {
            font-size: 1.25em !important;
            line-height: 1.2em;
        }
        .golem-forum-on-active-ignore {
            font-size: 1.2em !important;
            line-height: 1.2em;
        }
        .links {
            font-size: 1em !important;
            font-weight: bold;
            width: 100%;
        }
        /*search*/
        
        .text-alts {
            padding: 0.1cm 0 0 4em !important;
        }
        /*reply*/
        
        .forum #post #body,
        .forum #profile #body,
        .forum select {
            font-size: 1em !important;
            height: 8em !important;
        }
    }
}
