- updated 31.05.2015

Нічна тема для мудла Інституту інформатики
- блок "Навігація" ЗАВЖДИ зліва від головного контенту (і до авторизації, і після);
- блок завантаження файлів на всю ширину;

css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.moodle.ii.npu.edu.ua") {
  body {
    font-family: "Carrois Gothic","Helvetica",arial,serif !important;
    font-size: 15px;
    color: #5D6F6D;
    border-top: 3px solid #222;
    background: #333;
    background-image: url('http://www.transparenttextures.com/patterns/az-subtle.png');}
  #page-header {
    padding: 12px 0px 0px;
    border-bottom: #222;
    background-color: #222;
    background-image: url('http://www.transparenttextures.com/patterns/tactile-noise-light.png');}
  #headerlogo {
    font-size: 4.0em;
    top: 0px;
    margin-right: 5px;
    color: #ccc;
  }
  h1#title {
    color: #ccc;}
  #socialheading {
    color: #ccc;
    margin: 0;
  }
  .socials a {
    font-size: 2.0em;
  }
  .navbar-inner {
    border-top: none !important;
    border-bottom: none !important;
    border-left: none !important;
    border-right: none !important;
    background-color: rgba(0,0,0,0.1) !important;
    background-image: none;
    filter: none;}
  .navbar .nav {
    border-right: none;}
  h2.pagetitle span {
    background: transparent;
  }
  h2.pagetitle {
    font-size: 20px !important;
    background: none;
    text-align: center;
  }
  .block, .course-content ul li.section.main {
    padding: 8px;
    border-radius: 0;
    border: none;
    margin-bottom: 10px;
    background-color: rgba(27, 27, 32, 1);
    background-image: url('http://www.transparenttextures.com/patterns/outlets.png');
    box-shadow: 0 0 4px 1px #222;
    opacity: 0.7;
    transition: 0.3s all;}
  .block:hover, .course-content ul li.section.main:hover{opacity:1;}
  .breadcrumb > li {
    text-shadow: none;
  }
  a, a:visited {
    color: rgba(67, 103, 129, 1);}
  a:hover, a:focus {
    color: rgba(63, 117, 156, 1);
    text-decoration: none;}
  #page-footer {
    background: rgba(0,0,0,0.1);
    font-size: 15px;
    border-top: none;
    text-align: left;}
  #region-main{float:right;}
  #block-region-side-pre{float:left;}
  .forumpost, .forumheaderlist {
    background: rgba(0,0,0,0.2);
    border-radius: 0;
    border: 1px solid #222 !important;
    padding: 10px;
    box-shadow: 0 0 7px 0px #111;
    transition: 0.3s all}
  .forumpost:hover, .forumheaderlist {box-shadow: 0 0 8px 1px #111;}
  
  .block .content {
    padding: 0px;
  }
  .coursebox {
    margin: 0px;
    padding: 0px !important;
  }
  .block_course_overview .content h2.title {
    width: 100%;
    margin: 0;
    overflow-x: hidden;
  }
  .block_course_overview .content h2.title a{
    display: block;
    line-height: 300%;
    font-size: 110%;
    width: 100%;
    padding-left: 20px;
    padding-right: 40px;
    transition: all 0.5s;
  }
  .block_course_overview .content h2.title a:hover{
    background-color: #888;
    color: #111;
  }
  .coursebox {
    border: none;
  }
  .block_course_overview .activity_info {
    padding-left: 40px;
  }
  
  
  table#form td.submit, .form-buttons, .path-admin .buttons, #fitem_id_submitbutton, .fp-content-center form + div, div.backup-section + form, #fgroup_id_buttonar {
    border-top: none !important;
    background-color: transparent !important;
    text-align: center;
    padding: 10px;
    margin-top: 0;
    margin-bottom: 0;
  }
  .form-item .form-setting, .form-item .form-description, .mform .fitem .felement, #page-mod-forum-search .c1, .mform .fdescription.required, .userprofile dl.list dd, .form-horizontal .controls{
    margin-left: 0;
  }
  .form-item .form-setting, .form-item .form-description, .mform .fitem .felement, #page-mod-forum-search .c1{
    margin-left:0;
  }
  .filemanager .fp-navbar {
    border-color: transparent;
    background: none repeat scroll 0% 0% rgba(20,20,20,0.4);
  }
  .fp-pathbar {
    border-top: 1px solid #191919;
  }
  .filepicker-filelist, .filemanager-container {
    background: none;
    border: #242424 1px solid;
  }
  #filesskin .yui3-panel-content {
    box-shadow: 5px 5px 20px 0px #000;
  }
  hr {
    display: none;
  }
}

