style_setting_options.value:
/*FFS*/
.project-card-content {
    opacity: 1;
    transition: 2s;
    transition-delay: 1s;
}

.project-thumbnail:hover ~ .project-card-content {
    opacity: 0;
    transition: 0.2s;
    transition-delay: 0.5s;
}

div.project-thumbnail {
    position: relative;
    transform: rotate(0deg);
    width: 100%;
    height: auto;
    transition: 2s;
    z-index: 0;
    left: 0;
}

div.project-thumbnail:hover {
    position: relative;
    transform: rotate(/*[[revolvedeg]]*/deg);
    width: /*[[revolvewidth]]*/%;
    height: auto;
    transition: all /*[[animationsec]]*/s ease 0.5s;
    z-index: 999;
    left: -25%;
}

div.project-thumbnail.left.mobile-clear.p1{
    position: static;
    transform: rotate(0deg);
    width: auto;
    height: auto;
}
