css:
@-moz-document url-prefix("http://guildwarstemple.com/dragontimer/") {
/* Ver 1.1: This hides the chest icon (Rare Item Status) in the bottom right corner of each timer */
a.form_box_r {
  display: none !important;
}

/* Ver 1.1: Hides the Temple labels since they have no timer and with the chest icon removed it's just a floating label */
.bonusChest.eventTimeBox {
  display: none !important;
}

#temple-info {
  display: none !important;
}

#timer-ads-lgbox {
  display: none !important;
}

#info-n-guesting {
  display: none !important;
}

#colophon {
  display: none !important;
}

#branding {
  display: none !important;
}

.app #usable-space {
  background: black !important;
}

body {
  background: #000000 !important;
}

hr {
  background-color: #000 !important;
}

#top-content {
  display: none !important;
}

.eventTimeBox {
  position: relative;
  float: left;
  clear: left;
  width: 287px;
  height: 96px;
  margin: 0 0 20px 0;
  background-color: #000000;
  background-image: none !important;
  color: #FFF4D6;
  font-weight: inherit;
  font-family: 'Architects Daughter', cursive;
  line-height: inherit;
  display: block;
}
}
