@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("freesoft.ru") {
  /*Реклама*/
  div[id*="banner"], div[class*="banner"], .block, #very_top, #title, #news_inf_new, .down_desc_likes, #footer, #sec_center_right, .back_but, #ban_top, #center, .r_block ol, #right {display: none !important;}
  div[style="padding: 30px 0px;"] {padding: 0 !important;}
  div[style="padding:30px 0;"] {display: none !important;}
  /**/
  html, body {background: url(http://ya-vidoc3000.narod.ru/anime/16/1.jpg) no-repeat 100% 100% fixed !important; -webkit-background-size: cover !important; background-size: cover !important; color: white !important;}
  a {color: white !important;}
  /*Header*/
  #search {background: none !important;}
  .h_right_content ul, #very_top_468 {display: none !important;}
  /*Content*/
  #item_center h1 {color: red !important;}
  .items {background-color: black !important; opacity: 0.7 !important;}
  .items .item:hover {background: none !important;}
  .musthave_inf_big_left {background-color: black !important;}
  .mhib_item span {background: none !important;}
  .mhib_item {border-top: 1px solid red !important; padding: 0 !important; margin: 2px !important;}
  .musthave_inf_big_title {border: none !important;}
  .mhib_item_right {padding: 0 !important;}
  .show_more {margin: 0 !important;}
  .musthave_inf_big_right {background-color: black !important; opacity: 0.6 !important;}
  #count_prog {border: none !important;}
  #articles {background-color: black !important;}
  .article span {background: none !important;}
  .mhib_item_right span {background: none !important;}
  #navi_tab_border {border: none !important;}
  /*Menu*/
  #nav > li {border: none !important; margin: 0 !important;}
  #nav > li > a {background-color: black !important; border: none !important;}
  #nav li ul li {background-color: black !important;}
  /*Top*/
  .r_block ol {border: none !important; background-color: black !important; opacity: 0.7 !important;}
}
