css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.primewire.ag"), domain("www.primewire.org"), domain("www.primewire.is") {
  @media only screen and (min-width:1200px) {
    .container {
      width: 96%;
      margin: 0 auto;
    }
  }
  .header { width: auto; background-repeat: no-repeat}
  .main_body { width: auto; background: url(data:image/jpg;base64,) repeat-y left top, #FFF url(data:image/jpeg;base64,) repeat-y right top}
  .col1 { width: calc(100% - 330px) }
  .index_container .index_item+.clearer { display: none }
  .index_item { height: auto !important; height: 306px; min-height: 306px; }
  .comment_box_info { float: none; width: auto; padding-right: 70px; }
  .comment_box_info table { width: 100% !important;}
  .footer { width: auto; background: url(data:image/jpg;base64,) no-repeat left top, url(data:image/jpg;base64,) no-repeat right top, url(data:image/jpg;base64,) repeat-x}
}
