css:
@-moz-document url-prefix("http://www.clickcritters.com/") {
megaWrapper {
	background-image:none !important;
}

#imgHeaderLeft {
	margin: 0px !important;
	float: left !important;
	height: 4px !important;
}

#megaHeader { 
  	display: none !important; 
}

#imgHeaderRight {
	height: 0px !important;
}


a:hover { 
  color:#47476B; 
  text-decoration:none !important;
}

#megaAd {
	/*--display: none !important;--*/
   	width: 160 px !important;
	background-color: #B4B4DC !important; 
	vertical-align: top !important;
}

#megaTopNav {
    background-color:#47476B !important;
  	font-size: 11px !important; 
}

#megaSidebar {
	background-color: #47476B !important;
  	font-size: 11px !important; 
}

#shoutbox {
 	font-size: 11px !important; 
  
}

#megaUserinfo {
	background-image: none !important;
  	background-color: #ADADC9 !important;
}


#megaContent,#megaFooter {
	background-color: #B4B4DC !important; 
  	font-size: 11px !important; 
}

#megaFooter {
	padding-left:15% !important;
}
  
#megaBottomLeft {
	background-color: #47476B !important;
}

#megaBottomLeftImg {
	background-image: none !important; 
}

#megaBottomRight {
	width: 160px !important;
	background-color:#B4B4DC !important; 
}

#megaBottomRightImg {
	background-image: none !important;
}


h1 {  
  background-color:#7575a3 !important; 
  border: 2px solid #47476B !important; 
}
  

#topNavLinks li ul.subnav  {  
	background:#7575a3 !important;
  	border: 2px solid   #47476B !important;
}

#topNavLinks li ul.subnav li{
	margin: 0; padding: 0 !important;
	border-top: 1px solid #47476B !important; /*--Create bevel effect--*/
	border-bottom: 1px solid #aeb3d7 !important; /*--Create bevel effect--*/
	clear: both !important;
	width: 170px !important;
	height: 20px !important;
}

#topNavLinks li ul.subnavi li a {
	color:#ebedfc !important;
}

html #topNavLinks li ul.subnav li a:hover {
	background-color: #47476B !important; 
  	color:#ebedfc !important;
}

div.forum { 
  	background-image: none !important;
  	background-color: #7575a3 !important;
}

div.forum div {  
  	background-image:none !important;
}

div.forum a {
  	color:#0f0f2a !important;
}

div.category  {  
	vertical-align:center; 
  	background-image: none !important; 
  	background-color:#47476B !important;
}

div.category div {  
  	background-color:#B4B4DC !important; 
  	background-image: none !important;  
}

#forumtable tbody {
  	background-color:#47476B !important;
}

#forumtable tr.unread { 
  	background-color:#7575A3 !important; 
  	
}

#forumtable tr .off { 
  	background-color:#d8d8ea !important;  
}

#forumtable tr a:hover {
  	color:#fff !important;     
  	text-decoration: none !important;
}

#forumtable tr:hover { 
  background-color:#8585b2 !important;
}

form input {
  background-color:#e7e7f8 !important;
}


#megaSidebar div #shoutbox {
	color: #ebedfc !important;
}

#megaSidebar div #shoutboxtext div {
	color: black !important;
	background-color:#ebedfc !important;
}

textarea, select, input {
  background-color: #ebedfc !important;     
  font-family: verdana !important;
}

input[type=submit] {
    border-radius: 6px !important;
    background: linear-gradient(#ebedfc, #a2a2c7) !important;
}

#mail_heading tr, #mail_heading td {
	background-color:#ebedfc !important; 	
	border:	#47476B !important; 
}
}
