css:
@-moz-document domain("last.fm"), domain("lastfm.es"), domain("lastfm.de"), domain("lastfm.fr"), domain("lastfm.jp"), domain("lastfm.it"), domain("lastfm.pl"), domain("lastfm.com.br"), domain("lastfm.se"), domain("lastfm.tr"), domain("lastfm.ru"), domain("cn.last.fm") {
/***** remove ads *******************************************/

.LastAd, .LastAd * {
	display: none;
}

/***** misc and backgrounds **********************************/

* {
	text-shadow: none !important;
	border-color: #1a1a1a !important;

}  
  

body { 
	background: #1a1a1a !important;
	color: #ccc;
}

img.icon { background-image: url('http://i.imgur.com/TNPDkD2.png') }

.toggle-list-current + li .btn {
	box-shadow: none;
}

.catalogue-scrobble-graph-container rect {
	fill: rgb(30, 30, 30) !important;
}

.stationbuttonMedium {
        border-radius: 10px;
}

.fiflufi .leftColWrapper {
        border-right: 1px dotted #323232;
}

a.ontour {
        border-color: #D51007 !important;
}

.tasteometer .super span {
        background: #b9245c;
}

.artist-grid-item {
        border: none;
}

/** fix border-radius **/

.similar-artist,
.album-item-cover,
.eventToggle li span,
.stationbuttonLarge,
a.lfmAddButtonLarge,
a.lfmAddButtonLarge span, 
a.lfmAddButtonLarge strong {
        border-radius: 10px;
}

/** #000 **/

table.tracklist tbody tr:hover, 
table.tracklist tbody tr.open,
table.chart tbody tr:hover td,
table.chart tbody tr.open td {
	background: #1a1a1a;
}


/** #1a1a1a **/

#content,
#pages .page,
#libraryNavigation .current,
#userMetadata div.itemContent,
#editor #editorPreviewPane,
.fiflufi .leftCol,
.content,
.recent-album-title,
.noobstrap .box,
.filterTags li a span,
.lfmBlack #page h3, 
.lfmBlack #page h3 a,
div.tertiaryNavigation ul li.current a,
div#trackResults table td {
	background: #1a1a1a;
}

.masthead {
	text-shadow: none!important;
	border: none!important;
	border-bottom: 1px solid #1a1a1a!important
 }
 
 
	.user-badge:after, .masthead, .btn.btn--header, .btn.user-dropdown-toggle {box-shadow: none !important;}
 
header.masthead.masthead--logged-in, 
a.btn.btn--small.btn--header.user-badge, 
a.user-dropdown-toggle.btn.btn--small.btn--header.btn--icon-only.needs-js.iconright.iconright--dropdown.menu-toggle, 
ul#user-dropdown.hidden-menu.dropdown-btn-menu.user-dropdown,
header.masthead,
a#login-link.btn.btn--small.btn--header.btn--rounded.login {
	color: #fff!important;
	background-color: #fff !important;
	background-image: linear-gradient(#1a1a1a, #1a1a1a) !important;
	box-shadow: none !important;
	text-shadow: none!important;

 }
 
.site-search .search-box {
	background: #1a1a1a !important;
	color: #ffffff !important;
	border: rgb(0, 0, 240) !important;
	box-shadow: none !important;

}
  

  
.site-footer {
	background: #1a1a1a;
	text-shadow: none;
}

div.fiflufi #content div.clearit {
	background: #1a1a1a !important;
}

div.errorMessage span.messageWrapper, 
div.dialogErrorMessage {
        background: url(http://cdn.lst.fm/flatness/messageboxes/error.png) 0.5em center no-repeat #1a1a1a;
}


/** #202020 **/

table.tracklist tr.odd,
table.chart tr.odd td,
table.forumtable tr.alt td,
div.newbieMessage,
div.rightCol div.nodataMessageBlock, 
div.rightCol div.nodataMessage span.messageWrapper,
div#topResult,
div.results,
div.infoMessage span.messageWrapper, 
div.infoMessageBlock,
ul.usersMedium li.findfriends div,
ul.forumComments li.odd,
ul.lfmDropDownBody li a,
ul#freeTracks li.odd,
.freeTracks li.odd, 
.topTracks li.odd,
li.rankItem:hover,
.ie li.rankItem:hover .multiButtonIcons,
input[type="submit"],
input[type="button"],
form.singleAction,
#tasteCocktail .wrapper,
#neighboursList li .sharedArtists,
#music-landing ul.artistList li.odd,
#userMetadata div.items ul li.odd,
#eventTags,
#recs #artistRecs div.similarArtists,
#primary,
#footerStuff,
#footer_content,
#forumsearch,
#secondaryNavigation li a:hover, 
#secondaryNavigation li.current a,
div#trackResults table tr.odd td,
.dialogBox,
.bbcode span.quote,
.recent-tracks-item,
.memo,
.noobstrap,
.actions-bar,
.secondary-nav .hidden-menu,
.lfmBlack .site-search .search-box,
.tagHead,
.feedback-message,
.recommendations table.eventsMedium tr.recommended td,
.filterTags li a,
.tasteometer .bar,
.catalogue-scrobble-graph, 
.catalogue-scrobble-graph-inner-wrapper,
.chart-item:nth-child(even),
.dialogBox .dialogForm,
.dialogBox .dialogButtons,
.dialogBox fieldset.resource, 
.dialog-attachment,
.dialogBox fieldset.resource .resource, 
.dialog-attachment .resource,
.typeahead,
.inset,
.more-of--grey,
.collapsibleBox-body-inner2,
.collapsibleBox-heading-openclose a,
.catalogue-social .btn--white,
.userResults li div.userContainer,
.page-head-bar {
	background: #202020;
}

.btn {
	background-color: #202020;
	background-image: linear-gradient(#202020, #202020);
	box-shadow: none;
}

.btn:hover {
	background-image: linear-gradient(#202020, #202020);
	box-shadow: none;      
}

.dropdown-btn-wrapper .btn {
	box-shadow: none;
}

input[type=text], input[type=password], textarea {
	background: #202020;
	border: 1px solid #323232;
	color: #ccc;
}

#libraryNavigation li {
	background: #202020;
	border-radius: 10px 10px 0 0;
}

div.tertiaryNavigation ul li a {
	background: #202020;
	border-radius: 10px 10px 0 0;
}

.collapsibleBox-heading h3 {
        background: #202020 !important;
}

#secondaryNavigation li a {
        background-color: #202020;
        background-image: -webkit-linear-gradient(top, #202020, #202020 70%);
}

#community-header {
        background: url(http://i.imgur.com/QL8kLub.jpg) 0 0 no-repeat #282928;
}

a.button, input.button, button.button, a.confirmButton, input.confirmButton, button.confirmButton {
        background: #202020;
        color: #ccc;
}

.listener-details::after {
        border-color: #1a1a1a;
}

/** #282828 **/

table.tracklist td.highlight {
	background: #282828;
}


/** #323232 **/

.whittle-pagination .btn.whittle-pagination-selected {
	background: #323232;
	box-shadow: none;
}


/** #d13636 **/

#page div.rightCol div.recommendedContent h2.heading {
	background: #d13636;
}


/** #0187c5 **/

div.chartbar span,
.tasteometer .low span,
.embedDialog .infoMessage, 
.modulePreferences .infoMessage {
	background: #0187c5;
}

/** #2486b9 **/
.notice, .noticeBar,
div.warnMessage span.messageWrapper, 
div.warnMessageBlock,
div.infoBox, 
p.infoBox, 
ul.infoBox {
        background: #2486b9;
}

.notice h3, 
.notice h3 a {
        background: #2486b9 !important;
}

/** transparent **/

.pictureFrame .overlay,
.catalogue-social,
.featured-tracks,
.fiflufi #content .fullWidth,
.intro,
.rankedChart .s-nodata,
.collapsibleBox-heading-openclose a:before, 
.collapsibleBox-heading-openclose a:after,
#libraryNavigation a, 
#libraryNavigation .current a,
#music-landing p.listfilter,
#smallPoster,
#recs h1.heading,
div.tertiaryNavigation ul li, 
div.tertiaryNavigation ul li.current,
div.fiflufi div.searchHeader,
div.typeaheadHint {
	background: transparent;
}

.recommended-for-you .search-box, 
.intro-search-container .search-box {
        background: transparent;
        box-shadow: none;
}

/***** font color ******************************************/

/** #ccc **/

h1, h2, h4, .h2Wrapper a {color: #b9245c !important;}

#taste .libraryInfo a.total,
#librarySubNav span.current a,
#libraryTopWrapper h2,
#page .stationbuttonInline,
#venue a,
#recs #artistRecs h2 a.name,
#userMetadata div.stations ul li a, 
#nowPlayingMeta div.metadataHeader h1 a,
#nowPlayingMeta div#recommendedStations strong,
#footerStuff strong,
#footerLinks dt,
#searchfilter li.current a,
#editor .bbbuttons li span,
#music-landing ul.artistList .name,
#community-header h2,
#neighboursList li .container .summary a,
.lfmBlack h2, 
.lfmBlack .h2,
.lfmBlack #page h2.heading,
.lfmBlack #page .h2Wrapper a,
.lfmBlack .site-search .search-box,
.listening-now-track, 
.listening-now-track a, 
.listening-now-track a:hover,
.language-selector a, 
.language-selector a:hover, 
.language-selector a:focus,
.promo-unit, 
.site-footer-nav a, 
.site-footer-nav a:hover,
.site-footer .call-to-action,
.site-footer-strapline,
.site-footer h2,
.journals h2 a,
.shouts .message p,
.pagehead h1,
.r-tag .catalogueHead h2, 
.recentAlbums li .album, 
.freeTracks li a, 
.topTracks li a,
.rankItem-title,
.filterTags li a,
.secondary-nav .current a, 
.secondary-nav .current a:hover,
.typeahead,
.resource p, .resource div,
.intro,
.collapsibleBox-heading-openclose a,
.memo--link p, 
.memo--link:hover p, 
.memo--link:focus p,
.albumsMedium .resContainer a,
.albumsSmall .resContainer a,
.notice, .noticeBar,
.recently-viewed h2,
.tagHead h2,
table.tracklist td.subjectCell,
table.tracklist th, table.tracklist td,
table.chart td.subjectCell a,
table.tracklist a,
table.forumtable td a.author,
div.tertiaryNavigation ul li.current a,
div.pagehead h1,
div#trackResults table td.artist a,
div.wikiStyleGuide,
ul.groupsSmall a strong,
ul.artistsLarge a,
ul.eventsSmall li strong.summary,
ul.eventsSmall li small.location,
ul.usersMedium li.findfriends div h2,
ul.usersMedium strong a,
ul.tagList li a,
ul#freeTracks a.trackTitle,
ul#freeTracks a.trackArtist,
ul.artistRecs li a strong.name,
a.promo-unit:hover, 
a.promo-unit:focus,
p#uploadYourMusic,
p.typeaheadSuggestions span,
h1, .h1,
input[type="submit"],
input[type="button"] {
	color: #ccc;
}

.lfmBlack #page h3, 
.lfmBlack #page h3 a,
.anon-brand strong,
.subscriber .userType {
	color: #ccc !important;
}

ul.journalsSmall a strong {
	color: #ccc;
	text-decoration: underline !important;
}

.recommendedContent span {
        color: #fff;
}


/** #0187c5 **/

#page .stationbuttonInline:hover,
ul.usersSmall li strong a,
ul.artistsSquare strong,
ul.lfmDropDownBody li a,
ul.forumComments div.comment h4,
ul.groupsMedium strong a {
	color: #0187c5;
}

/** #df1c23 **/ 
.content .anon-brand {
        color: #df1c23;
}

/** #000 **/
.notice a,
.noticeBar a, 
div.warnMessage span.messageWrapper a, 
div.warnMessageBlock a, 
div.infoBox a, 
p.infoBox a, 
ul.infoBox a {
        color: #000;
}

/***** replace imgs *******************************************/

img[src="http://cdn.last.fm/flatness/global/icon_eq.gif"] {
	background: url('http://i.imgur.com/uMr1kYi.gif');
        height: 0 !important;
	width: 0 !important;
	padding-top: 12px !important;
	padding-left: 12px !important;
}

.recently-viewed.content img[src="http://cdn.lst.fm/flatness/responsive/5/noimage/default_user_60_g1.png"] {
	background: url('http://i.imgur.com/p4KQBjl.jpg');
        height: 0 !important;
        width: 0 !important;
        padding-top: 55px !important;
        padding-left: 55px !important;
}

.mpuTop.rightCol img[src="http://cdn.last.fm/flatness/responsive/2/noimage/default_user_60_g1.png"] {
	background: url('http://i.imgur.com/iZX31My.jpg') !important;
        height: 0 !important;
        width: 0 !important;
        padding-top: 34px !important;
        padding-left: 34px !important;
}

.skyWrap .userContainer img[src="http://cdn.last.fm/flatness/responsive/2/noimage/default_user_60_g1.png"] {
	background: url('http://i.imgur.com/HKTwJ8o.jpg') !important;
        height: 0 !important;
        width: 0 !important;
        padding-top: 64px !important;
        padding-left: 64px !important;
}

#editor .bbbuttons li span {
        background: url('http://i.imgur.com/zZwYQne.png') top right no-repeat;
}

#editor .bbbuttons li {
        background: url('http://i.imgur.com/yRfHEBx.png') top left no-repeat;
}

#secondaryNavigation img[src="http://cdn.last.fm/flatness/findfriends/lastfm.png"] {
        background: url('http://i.imgur.com/ef8RKzr.png');
        height: 0 !important;
        width: 0 !important;
        padding-top: 45px !important;
        padding-left: 90px !important;
}
}
